#include <iostream>
using namespace std;

const int INF = 2e9 + 5;
#include <vector>
#include <queue>

typedef vector<int> vi;
typedef vector<vi> vvi;

vvi johnny(vvi &g){
	vvi d(g.size(), vi(g.size(), INF));
	for(int s = 0; s < g.size(); s++){
		queue<int> q;
		d[s][s] = 0;
		q.push(s);
		while(!q.empty()){
			int curr = q.front();
			q.pop();
			for(int &x : g[curr])
				if(d[s][x] == INF)
					d[s][x] = d[s][curr] + 1, q.push(x);
		}
	}
	return move(d);
}

bool solve(vvi &g, vvi &d, int k, int x1, int x2, int y1, int y2, vector<vector<bool>> &memo){
	memo[x1][y1] = 1;
	//cout << x1 << ' ' << y1 << " @ " << d[x1][y1] << " / " << k << '\n';
	if(d[x1][y1] < k) return 0;
	
	if(x1 == x2 && y1 == y2) return 1;

	for(int x3 : g[x1]) if(!memo[x3][y1])
		if(solve(g, d, k, x3, x2, y1, y2, memo))
			return 1;

	for(int y3 : g[y1]) if(!memo[x1][y3])
		if(solve(g, d, k, x1, x2, y3, y2, memo))
			return 1;
	return 0;
}

int pianifica(int N, int M, int P1, int D1, int P2, int D2, int A[], int B[]) {
	vvi g(N);
	for(int i = 0; i < M; i++)
		g[A[i]].push_back(B[i]), g[B[i]].push_back(A[i]);

	vvi d = johnny(g);

	int l = 0, r =  min(d[P1][P2], d[D1][D2]);
	while(l < r){
		vector<vector<bool>> memo(N, vector<bool>(N, 0));
		int m = (l + r + 1)/2;
		//cout << "m = " << m << '\n';
		if(solve(g, d, m, P1, D1, P2, D2, memo))
			l = m;
		else
			r = m - 1;
	}

	return l;
}

