    public class MyTime
    {
        //Constructor with time parameters
        public MyTime(int hours, int minutes, int seconds)
        {
            Hours = hours;
            Minutes = minutes;
            Seconds = seconds;
        }
        //Empty constructor
        public MyTime()
        {

        }

        //Amount of hours stored in this time object
        public int Hours;
        //Amount of minutes stored in this time object
        public int Minutes;
        //Amount of seconds stored in this object
        public int Seconds;

        //Returns a new time object with time values from this object + amount of minutes as parameter
        public MyTime IncrementTime(int minutes)
        {
            //Return this object if incrementation is lower or equal to 0
            if(minutes <=0)
                return this;

            //The total amount of minutes
            int totalMinutes = (Hours * 60) + Minutes + minutes;

            //New amount of hours
            int newHours = (totalMinutes / 60) % 24;
            //New amount of minutes
            int newMinutes = (totalMinutes - (newHours * 60)) % 60;
            //New amount of seconds
            int newSeconds = Seconds;
            //Create new MyTime object
            return new MyTime(newHours, newMinutes, newSeconds);
        }

        //Returns a string in format: hours:minutes:seconds
        public string ToString()
        {
            return Hours.ToString() + ":" + Minutes.ToString() + ":" + Seconds.ToString();
        }
    }
