function productSlider() {
	// Owl Carousel DOM Elements
	var carousel1;
	var carousel2;

	$('ul li.product').each( function(i, el) {
		var element = $(el);

		var carousel1 = element.find('.clock-slider');
		var carousel2 = element.find('.dots-container');

		// Initialize plugin
		var owlCarousel1 = $(carousel1).owlCarousel({
			items: 1,
			slideSpeed : 0,
			mouseDrag: true,
			responsiveRefreshRate : 100,
			smartSpeed: 300,
			paginationSpeed : 600,
			nav: false,
			dots: false,
			navigation : false,
		});

		var owlCarousel2 = $(carousel2).owlCarousel({
			items: 1,
			mouseDrag: false,
			responsiveRefreshRate : 100,
			paginationSpeed : 300,
			dots: false,
			nav: false,
			responsiveClass:true,
		});

		// Sync thumbnails with slider & add current class
		carouselSyncCurrentClass(carousel2, owlCarousel1);

		// On slider change: Sync thumbnails & add current class		
		owlCarousel1.on('changed.owl.carousel', function () {
			updateDots();
		});

		// Thumbnails switch click event.
		carousel2.find('.thumbnail').click( function() {
			var element = $(this);
			var attribute = element.data('attribute');
			var color = element.data('color');
			var active = carousel2.find('.owl-item');
			var activeItem = element.parent();
			var itemIndex = owlCarousel1.find('img[data-linked_attribute='+attribute+']').parent().parent().index();

			owlCarousel1.trigger('to.owl.carousel', itemIndex);
			active.removeClass('active');
			owlCarousel1.parent('.image-container').find('.bgColor').css({ 'backgroundColor': '#' + color });
			activeItem.addClass('active');

		});
	});

	function carouselSyncCurrentClass(carousel2, owlCarousel1) {
		var attribute = carousel2.find('.active').data('attribute');
		var color = carousel2.find('.active').children('.thumbnail').data('color');
		var itemIndex = owlCarousel1.find('img[data-linked_attribute='+attribute+']').parent().parent().index();

		owlCarousel1.parent('.image-container').find('.bgColor').css({ 'backgroundColor': '#' + color });
	}

	function updateDots() {
		setTimeout( function() {
			var current = owlCarousel1.find('.owl-item.active');
			var currentIndex = current.index();
			var newDot = owlCarousel2.find('.owl-item:nth-child('+ (currentIndex + 1) +')');
			var color = newDot.children('.thumbnail').data('color');

			owlCarousel1.parent('.image-container').find('.bgColor').css({ 'backgroundColor': '#' + color });
			owlCarousel2.find('.owl-item.active').removeClass('active');
			newDot.addClass('active');
		}, 40);
	}
}
productSlider();

const conn = new Mongo("127.0.0.1:27017");
const db = conn.getDB("cubicl");
db.auth("app", "12345678");

var todos = db.todos.find({"category": {$ne: null}});

todos.forEach(function(todo) {
	var category = todo.category;
	var exists = db.todo_categories.find({_id: category}).count();

	if (!exists) {
		db.todos.updateOne({_id: todo._id}, {$set: {category: null}});
	}
});
