type code = Elem of bool | Not | And | Or | Xor;;
 
let eval list =
	let rec evalH list =
  	match list with
    | [Elem(fst:bool)] -> [Elem(fst:bool)]
    | Elem(fst:bool)::Not::tl -> 									evalH (Elem(not fst) :: tl)
    | Elem(fst:bool)::Elem(scd:bool)::Or::tl -> 	evalH (Elem(fst || scd) :: tl)
    | Elem(fst:bool)::Elem(scd:bool)::And::tl -> 	evalH (Elem(fst && scd) :: tl)
    | Elem(fst:bool)::Elem(scd:bool)::Xor::tl -> 	evalH (Elem((fst || scd) && (fst <> scd)) :: tl)
    | _ -> failwith "Nieprawidlowe wyrazenie"
	in match evalH list with
	| [Elem true] -> true
	| [Elem false] -> false;;
 
let instr1 = [Elem true; Elem false; And; Not; Elem false; Xor];;
let instr2 = [Elem true; Elem false; Or; Not];;
let instr3 = [Elem true; Elem false; And; Or];;
let instr4 = [Elem true; Not; Elem false; Xor; Elem true; And];;
let instr5 = [And];;
let instr6 = [Elem false; Not; Not];;
let instr7 = [];;

eval instr1;;
eval instr2;;
eval instr3;;
eval instr4;;
eval instr5;;
eval instr6;;
eval instr7;;
