//Method to encrypt the password
private String encryptAES(String key1, String key2){
        Log.i("KEYS", key1 + "\n" + key2);
        try {
            SecretKeySpec key = new SecretKeySpec(hexStringToByteArray(key1), "AES");
            Cipher cipher = Cipher.getInstance("AES/ECB/PKCS7Padding");


            cipher.init(Cipher.ENCRYPT_MODE, key);
            return byteArrayToHex(cipher.doFinal(hexStringToByteArray(key2)));
        }catch (Exception ex){
            Log.i("ENCRYPT", "EXCEPTION "+ex.toString());
            return null;
        }
    }

//Hex String to byte array method
public static byte[] hexStringToByteArray(String s) {
        try {
            int len = s.length();
            byte[] data = new byte[len/2];
            for(int i = 0; i < len; i+=2){
                data[i/2] = (byte) ((Character.digit(s.charAt(i), 16) << 4) + Character.digit(s.charAt(i+1), 16));
            }
            return data;
        } catch (Exception e) {
        }
        return new byte[]{00};
    }

