    public void showJob(final String _id) {
        final DatabaseReference node_jobs = FirebaseDatabase.getInstance().getReference("Jobs");
        final AlertDialog.Builder alert = new AlertDialog.Builder(this, android.R.style.Theme_Black_NoTitleBar_Fullscreen);
        LayoutInflater inflater = this.getLayoutInflater();
        View view = inflater.inflate(R.layout.layout_info_job, null);
        alert.setView(view);
        final TextView tv_name = (TextView) view.findViewById(R.id.tv_name);
        final TextView tv_store = (TextView) view.findViewById(R.id.tv_store);
        final TextView tv_address = (TextView) view.findViewById(R.id.tv_address);
        final TextView tv_description = (TextView) view.findViewById(R.id.tv_description);
        final TextView tv_expired = (TextView) view.findViewById(R.id.tv_expired);
        final TextView tv_experience = (TextView) view.findViewById(R.id.tv_experience);
        final TextView tv_jobtype = (TextView) view.findViewById(R.id.tv_job_type);
        final TextView tv_request = (TextView) view.findViewById(R.id.tv_request);
        final TextView tv_quantity = (TextView) view.findViewById(R.id.tv_quantity);
        final TextView tv_benefit = (TextView) view.findViewById(R.id.tv_benefit);
        final Button bt_apply = (Button) view.findViewById(R.id.bt_apply);

        node_jobs.child(_id).addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Job job = dataSnapshot.getValue(Job.class);
                if (job != null) {
                    tv_name.setText(job.getJobName());
                    tv_store.setText(job.getEmployerName());
                    tv_address.setText(job.getPlace());
                    tv_description.setText(job.getDescription());
                    tv_expired.setText(job.getDeadline());
                    tv_experience.setText(job.getExperience());
                    tv_jobtype.setText(job.getJobType());
                    tv_request.setText(job.getRequest());
                    tv_quantity.setText(job.getQuantity());
                    tv_benefit.setText(job.getBenefit());
                    dataSnapshot.getRef().child("apply").child(FirebaseAuth.getInstance().getCurrentUser().getUid()).addListenerForSingleValueEvent(new ValueEventListener() {
                        @Override
                        public void onDataChange(DataSnapshot dataSnapshot) {
                            User user = dataSnapshot.getValue(User.class);
                            if (user != null) {
                                bt_apply.setVisibility(View.GONE);
                            } else {
                                bt_apply.setVisibility(View.VISIBLE);
                            }
                            alert.show();
                        }

                        @Override
                        public void onCancelled(DatabaseError databaseError) {

                        }
                    });
                }
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {

            }
        });
        bt_apply.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                User user = new User();
                user.setId(FirebaseAuth.getInstance().getCurrentUser().getUid());
                node_jobs.child(_id).child("apply").child(FirebaseAuth.getInstance().getCurrentUser().getUid()).setValue(user).addOnCompleteListener(new OnCompleteListener<Void>() {
                    @Override
                    public void onComplete(@NonNull Task<Void> task) {
                        if (task.isSuccessful()) {
                            bt_apply.setText("Applied");
                            bt_apply.setEnabled(false);
                            Toast.makeText(getApplicationContext(), "Apply success", Toast.LENGTH_SHORT).show();
                        } else {
                            Toast.makeText(getApplicationContext(), "Apply fail", Toast.LENGTH_SHORT).show();
                        }
                    }
                });
            }
        });

    }





