#include <bits/stdc++.h>

#define ll long long
#define ld long double
#define pb push_back
#define F first
#define S second
#define pp pair < pair < int, int >, int >
#define endl '\n'

using namespace std;

vector <int> g[555];
vector <vector <int>> ans;
map <string,int> mp;

int main() {
    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
#ifdef LOCAL
    freopen ( "input.txt", "r", stdin );
    freopen ( "output.txt", "w", stdout );
#endif
    int n;
    cin>>n;
    vector <string> kekos;
    for ( int i = 0; i < n; i++ ) {
        string s;
        cin>>s;
        mp[s] = i;
        kekos.pb(s);
    }
    for ( int i = 0; i < n; i++ ) {
        string s;
        int m;
        cin>>s>>m;
        int v = mp[s];
        for ( int j = 0; j < m; j++ ) {
            string ss;
            cin>>ss;
            cin>>ss;
            int vv = 0;
            while ( ss.back() == ',' ) {
                ss.pop_back();
                vv = mp[ss];
                g[v].pb(vv);
                cin>>ss;
            }
            vv = mp[ss];
            g[v].pb(vv);
        }
    }
    for ( int i = 0; i < n; i++ ) {
        vector <int> used(n,-1);
        queue <int> q;
        used[i] = i;
        q.push(i);
        int posl = i;
        int f = 0;
        while ( !q.empty() ) {
            int v = q.front();
            q.pop();
            for ( auto &to: g[v] ) {
                if ( used[to] == -1 ) {
                    q.push(to);
                    used[to] = v;
                    continue;
                }
                if ( to == i ) {
                    posl = v;
                    f = 1;
                    break;
                }
            }
        }
        if ( !f ) continue;
        vector <int> serf;
        int v = posl;
        while ( v != i ) {
            serf.pb(v);
            v = used[v];
        }
        serf.pb(i);
        if ( serf.size() > 0 ) {
            ans.pb(serf);
        }
    }
    int mn = 1e9;
    for ( auto &i: ans ) {
        mn = min( mn, int(i.size()) );
    }
    for ( auto &i: ans ) {
        if ( i.size() == mn ) {
            for ( int j = i.size()-1; j >= 0; j-- ) {
                cout<<kekos[i[j]]<<' ';
            }
            return 0;
        }
    }
    cout<<"SHIP IT";
    return 0;
}
