#include <linux/clk.h>
#include <linux/clk-provider.h>
#include <linux/err.h>
#include <linux/io.h>
#include <linux/kernel.h>
#include <linux/of.h>
#include <linux/of_address.h>
#include <linux/slab.h>

#include "nexell_clk.h"

#define NEXPLL_CLKMODEREG0(pll) (pll->base_addr)
#define NEXPLL_PLLSETREG(pll) (pll->base_addr + 0x8 + pll->index * sizeof(u32))

#define NEXPLL_POWERDOWN	BIT(29)
#define NEXPLL_NBYPASS		BIT(28)

#define PLL_PDIV_SHIFT 0x12
#define PLL_MDIV_SHIFT 0x08
#define PLL_SDIV_SHIFT 0x00

#define PLL_PDIV_MASK GENMASK(23, PLL_PDIV_SHIFT)
#define PLL_MDIV_MASK GENMASK(17, PLL_MDIV_SHIFT)
#define PLL_SDIV_MASK GENMASK(7, PLL_SDIV_SHIFT)

struct nexell_clk_pll {
	u8 index;
	struct clk_hw   hw;
	void __iomem    *base_addr;
	spinlock_t      *lock;
};

#define to_clk_pll(hw) (container_of(hw, struct nexell_clk_pll, hw))

u32 get_best_pll_parameters(unsigned long drate, unsigned long prate)
{
	int i, j, mdiv, pdiv, sdiv;
	unsigned int diff_rate, arate;

	for (i = 3; i <= 6; i++) {
		for (j = 0; j < 4; j++) {
			mdiv = ((drate * i) << j) / prate;
			arate = (prate * mdiv) / (i << j);
			diff_rate = abs(arate) - abs(drate);
			if (diff_rate > 0)
				continue;
			pdiv = i;
			sdiv = j;
			if (drate >= (1300 * 1000 * 1000) && (sdiv == 1)) {
				pdiv *= 2;
				sdiv = 0;
			} else if (drate < (1300 * 1000 * 1000) && (!sdiv)) {
				mdiv *= 2;
				sdiv = 1;
			} else if (drate <= (600 * 1000 * 1000)) {
				if (sdiv == 1) {
					mdiv *= 2;
					sdiv = 2;
				} else if (sdiv == 3)
					sdiv = 2;
			}

			return ((pdiv << PLL_PDIV_SHIFT) |
				(sdiv << PLL_SDIV_SHIFT) |
				(mdiv << PLL_MDIV_SHIFT));
		}
	}

	return 0;
}

unsigned long calculate_pll_freq(u32 reg, unsigned long parent_rate)
{
	u32 pdiv, mdiv, sdiv;
	u64 fvco = parent_rate;

	mdiv = (reg & PLL_MDIV_MASK) >> PLL_MDIV_SHIFT;
	pdiv = (reg & PLL_PDIV_MASK) >> PLL_PDIV_SHIFT;
	sdiv = (reg & PLL_SDIV_MASK) >> PLL_SDIV_SHIFT;

	fvco *= mdiv;
	do_div(fvco, (pdiv << sdiv));

	return (unsigned long)fvco;
}

static unsigned long nexell_pll_recalc_rate(struct clk_hw *hw,
					    unsigned long parent_rate)
{
	struct nexell_clk_pll *pll = to_clk_pll(hw);
	u32 pll_con = readl_relaxed(NEXPLL_PLLSETREG(pll));

	return calculate_pll_freq(pll_con, parent_rate);
}

static long nexell_pll_round_rate(struct clk_hw *hw,
				  unsigned long drate, unsigned long *prate)
{
	u32 pll_con = get_best_pll_parameters(drate, *prate);

	return calculate_pll_freq(pll_con, *prate);
}

static int nexell_pll_set_rate(struct clk_hw *hw, unsigned long rate,
			       unsigned long parent_rate)
{
	struct nexell_clk_pll *pll = to_clk_pll(hw);
	u32 reg;

	spin_lock(pll->lock);

	reg = readl_relaxed(NEXPLL_PLLSETREG(pll));
	reg &= ~((PLL_MDIV_MASK << PLL_MDIV_SHIFT) |
		 (PLL_PDIV_MASK << PLL_PDIV_SHIFT) |
		 (PLL_SDIV_MASK << PLL_SDIV_SHIFT));
	reg |= get_best_pll_parameters(rate, parent_rate);
	writel_relaxed(reg, NEXPLL_PLLSETREG(pll));

	reg = readl_relaxed(pll->base_addr);
	writel_relaxed(reg | BIT(pll->index), pll->base_addr);

	spin_unlock(pll->lock);

	return 0;
}

static int nexell_pll_clk_enable(struct clk_hw *hw)
{
	return -ENODEV;
}

static void nexell_pll_clk_disable(struct clk_hw *hw)
{
}

static int nexell_pll_clk_is_enabled(struct clk_hw *hw)
{
	struct nexell_clk_pll *pll = to_clk_pll(hw);
	u32 reg = readl_relaxed(NEXPLL_PLLSETREG(pll));

	return ((reg & (NEXPLL_POWERDOWN | NEXPLL_NBYPASS)) == NEXPLL_NBYPASS);
}

const struct clk_ops nexell_pll_ops = {
	.recalc_rate = nexell_pll_recalc_rate,
	.round_rate = nexell_pll_round_rate,
	.set_rate = nexell_pll_set_rate,
	.enable = nexell_pll_clk_enable,
	.disable = nexell_pll_clk_disable,
	.is_enabled = nexell_pll_clk_is_enabled,
};

static struct clk * __init
nexell_clk_register_pll(const char *clk_name, struct nexell_clk_pll *nex_pll,
			const char *parent_name)
{
	struct clk_init_data init;
	struct clk *clk;

	init.name = clk_name;
	init.parent_names = &parent_name;
	init.num_parents = 1;

	init.flags = CLK_IS_BASIC | CLK_GET_RATE_NOCACHE;
	init.ops = &nexell_pll_ops;

	nex_pll->hw.init = &init;

	clk = clk_register(NULL, &nex_pll->hw);
	if (IS_ERR(clk))
		return clk;

	pr_debug("%s: parent %s rate %lu\n",
		__clk_get_name(clk),
		__clk_get_name(clk_get_parent(clk)),
		clk_get_rate(clk));

	return clk;
}

struct nexell_pll_provider {
	struct clk_onecell_data	onecell;
	spinlock_t		shared_lock;
};

static void nexell_clk_init_plls(struct device_node *node)
{
	struct nexell_pll_provider *clk_data;
	struct nexell_clk_pll *nex_pll;
	void __iomem *base_addr;
	const char *parent_name, *clk_name;
	int num_clks, i;

	num_clks = of_property_count_strings(node, "clock-output-names");

	clk_data = kzalloc(sizeof(*clk_data), GFP_KERNEL);
	if (!clk_data)
		return;

	clk_data->onecell.clk_num = num_clks;
	clk_data->onecell.clks = kcalloc(num_clks, sizeof(struct clk *),
					 GFP_KERNEL);
	if (!clk_data->onecell.clks)
		goto err;

	nex_pll = kcalloc(num_clks, sizeof(*nex_pll), GFP_KERNEL);
	if (nex_pll == NULL)
		return;

	spin_lock_init(&clk_data->shared_lock);

	parent_name = of_clk_get_parent_name(node, 0);

	base_addr = of_iomap(node, 0);

	for (i = 0; i < num_clks; i++) {
		struct clk *clk;

		of_property_read_string_index(node, "clock-output-names",
					      i, &clk_name);
		nex_pll[i].base_addr = base_addr;
		nex_pll[i].index = i;
		nex_pll[i].lock = &clk_data->shared_lock;
		clk = nexell_clk_register_pll(clk_name, nex_pll, parent_name);
		if (IS_ERR(clk))
			goto err;

		clk_data->onecell.clks[i] = clk;
	}

	of_clk_add_provider(node, of_clk_src_onecell_get, &clk_data->onecell);
	return;

err:
	kfree(clk_data->onecell.clks);
	kfree(clk_data);
}

CLK_OF_DECLARE(nexell_plls, "nexell,s5p-pll-clocks", nexell_clk_init_plls);

declare function require(x: string): any;

import * as THREE from 'three';

const Stats = require('stats-js');


import 'imports-loader?THREE=three!../../node_modules/three/examples/js/controls/OrbitControls';

import GPUComputationRenderer from './GPUComputationRenderer';
const TimelineMax = require('gsap/TimelineMax');
const GPUParticleVert = require('./GLSL/GPUParticle.vert');
const GPUParticleFrag = require('./GLSL/GPUParticle.frag');
const VelocityFrag = require('./GLSL/ComputeVelocity.frag');
const PositionFrag = require('./GLSL/ComputePosition.frag');
const monalisa = require('./texture/MonaLisa.png');




export default class ParticleGallerySystem
{

    private WIDTH = 250;
    private PARTICLES = this.WIDTH * this.WIDTH;

    // メモリ負荷確認用
    // private stats:any;

    // 基本セット
    private container;
    private camera:THREE.PerspectiveCamera;
    private scene:THREE.Scene;
    private renderer:THREE.WebGLRenderer;
    private geometry:THREE.BufferGeometry;
    private controls:THREE.OrbitControls;


    // gpgpuをするために必要なオブジェクト達
    private gpuCompute:any;
    private velocityVariable:any;
    private positionVariable:any;
    private positionUniforms:any;
    private velocityUniforms:any;
    private particleUniforms:any;
    private effectController:any;

    public imgWidth = 97;
    public imgHeight = 145;

    public texture:THREE.Texture;

    public isGpuUpdate:boolean = false;

    constructor(scene:THREE.Scene, camera:THREE.PerspectiveCamera, renderer:THREE.WebGLRenderer)
    {
        this.camera = camera;
        this.scene = scene;
        this.renderer = renderer;

        // this.init();

    }
    public setTexture(imgstr:string)
    {
        let img = new Image();
        img.src = imgstr;
        this.texture.needsUpdate = true;
        this.texture.image = img;
        this.texture.wrapS = THREE.ClampToEdgeWrapping;
        this.texture.wrapT = THREE.ClampToEdgeWrapping;



    }


    public init(width?:number,height?:number,textures?:THREE.Texture) {
        this.imgWidth = (width === undefined? 100 : width);
        this.imgHeight = (height === undefined? 100:height);

        // this.textures = (textures === undefined? :textures);

        this.texture = new THREE.Texture();
        this.setTexture(monalisa);



        window.addEventListener( 'resize', this.onWindowResize, false );

        // ①gpuCopute用のRenderを作る
        this.initComputeRenderer();

        // ②particle 初期化
        this.initPosition();
        // this.animate();

        this.update();

    }

    public initComputeRenderer() {

        // gpgpuオブジェクトのインスタンスを格納
        this.gpuCompute = new GPUComputationRenderer( this.WIDTH, this.WIDTH, this.renderer );

        // 今回はパーティクルの位置情報と、移動方向を保存するテクスチャを2つ用意します
        var dtPosition = this.gpuCompute.createTexture();
        var dtVelocity = this.gpuCompute.createTexture();

        // テクスチャにGPUで計算するために初期情報を埋めていく
        this.fillTextures( dtPosition, dtVelocity );

        // shaderプログラムのアタッチ
        this.velocityVariable = this.gpuCompute.addVariable( "textureVelocity", VelocityFrag, dtVelocity );
        this.positionVariable = this.gpuCompute.addVariable( "texturePosition", PositionFrag, dtPosition );

        // 一連の関係性を構築するためのおまじない
        let variables = [this.positionVariable, this.velocityVariable];
        this.gpuCompute.setVariableDependencies( this.velocityVariable, variables );
        this.gpuCompute.setVariableDependencies( this.positionVariable, variables );



        // error処理
        var error = this.gpuCompute.init();
        if ( error !== null ) {
            console.error( error );
        }
    }

    // restart用関数 今回は使わない
    public restartSimulation() {
        var dtPosition = this.gpuCompute.createTexture();
        var dtVelocity = this.gpuCompute.createTexture();
        this.fillTextures( dtPosition, dtVelocity );
        this.gpuCompute.renderTexture( dtPosition, this.positionVariable.renderTargets[ 0 ] );
        this.gpuCompute.renderTexture( dtPosition, this.positionVariable.renderTargets[ 1 ] );
        this.gpuCompute.renderTexture( dtVelocity, this.velocityVariable.renderTargets[ 0 ] );
        this.gpuCompute.renderTexture( dtVelocity, this.velocityVariable.renderTargets[ 1 ] );
    }

    // ②パーティクルそのものの情報を決めていく。
    public initPosition() {

        // 最終的に計算された結果を反映するためのオブジェクト。
        // 位置情報はShader側(texturePosition, textureVelocity)
        // で決定されるので、以下のように適当にうめちゃってOK

        this.geometry = new THREE.BufferGeometry();
        var positions = new Float32Array( this.PARTICLES * 3 );
        var p = 0;
        for ( var i = 0; i < this.PARTICLES; i++ ) {
            positions[ p++ ] = 0;
            positions[ p++ ] = 0;
            positions[ p++ ] = 0;
        }

        // uv情報の決定。テクスチャから情報を取り出すときに必要
        var uvs = new Float32Array( this.PARTICLES * 2 );
        var xywidth = new Float64Array( 3 );
        xywidth[2] = this.WIDTH;
        p = 0;
        for ( var j = 1; j < this.WIDTH; j++ ) {
            for ( var i = 1; i < this.WIDTH; i++ ) {
                xywidth[0] = i;
                xywidth[1] = j;
                uvs[ p++ ] = xywidth[0] / ( xywidth[2] )-(1.0/xywidth[2]);
                uvs[ p++ ] = xywidth[1] / ( xywidth[2] )-(1.0/xywidth[2]);
            }
        }

        // attributeをgeometryに登録する
        this.geometry.addAttribute( 'position', new THREE.BufferAttribute( positions, 3 ) );
        this.geometry.addAttribute( 'uv', new THREE.BufferAttribute( uvs, 2 ) );



        // uniform変数をオブジェクトで定義
        // 今回はカメラをマウスでいじれるように、計算に必要な情報もわたす。
        this.particleUniforms = {
            texturePosition: { value: null },
            textureVelocity: { value: null },
            pointSize:{value:1.5},
            corner:{value:1.0},
            texture01:{value:this.texture},
            cameraConstant: { value: this.getCameraConstant( this.camera ) }
        };



        // Shaderマテリアル これはパーティクルそのものの描写に必要なシェーダー
        var material = new THREE.ShaderMaterial( {
            uniforms:       this.particleUniforms,
            vertexShader: GPUParticleVert,
            fragmentShader: GPUParticleFrag
        });
        material.extensions.drawBuffers = true;
        var particles = new THREE.Points( this.geometry, material );
        particles.matrixAutoUpdate = true;
        particles.updateMatrix();
        particles.frustumCulled = false;

        // パーティクルをシーンに追加
        this.scene.add( particles );
    }


    public fillTextures( texturePosition, textureVelocity ) {

        // textureのイメージデータをいったん取り出す
        var posArray = texturePosition.image.data;
        var velArray = textureVelocity.image.data;

        // パーティクルの初期の位置は、ランダムなXZに平面おく。
        // 板状の正方形が描かれる
        let xCounter = 1;
        let yCounter = 1;
        let p = 0.0;
        for ( var k = 0, kl = posArray.length; k < kl; k += 4 ) {
            // Position
            xCounter ++;
            if(xCounter % this.WIDTH == 0)
            {
                yCounter++;
                p += 0.05;
            }
            var x, y, z;
            x = (-0.5 + (xCounter%this.WIDTH)/this.WIDTH)*this.imgWidth;
            y = (-0.5 + (yCounter%this.WIDTH)/this.WIDTH)*this.imgHeight;
            z = 0;
            // posArrayの実態は一次元配列なので
            // x,y,z,wの順番に埋めていく。
            // wは今回は使用しないが、配列の順番などを埋めておくといろいろ使えて便利
            posArray[ k + 0 ] = x;
            posArray[ k + 1 ] = y;
            posArray[ k + 2 ] = z;
            posArray[ k + 3 ] = 0;

            // 移動する方向はとりあえずランダムに決めてみる。
            // これでランダムな方向にとぶパーティクルが出来上がるはず。
            velArray[ k + 0 ] = Math.random()*2-1;
            velArray[ k + 1 ] = Math.random()*2-1;
            velArray[ k + 2 ] = Math.random()*2-1;
            velArray[ k + 3 ] = Math.random()*2-1;
        }
    }



    // カメラオブジェクトからシェーダーに渡したい情報を引っ張ってくる関数
    // カメラからパーティクルがどれだけ離れてるかを計算し、パーティクルの大きさを決定するため。
    public getCameraConstant( camera ) {
        return window.innerHeight / ( Math.tan( THREE.Math.DEG2RAD * 0.5 * camera.fov ) / camera.zoom );
    }



    // 画面がリサイズされたときの処理
    // ここでもシェーダー側に情報を渡す。
    public onWindowResize =()=> {
        // this.camera.aspect = window.innerWidth / window.innerHeight;
        // this.camera.updateProjectionMatrix();
        // this.renderer.setSize( window.innerWidth, window.innerHeight );
        this.particleUniforms.cameraConstant.value = this.getCameraConstant( this.camera );
    }


    public update =(time?)=> {
        if(this.isGpuUpdate)
        {
            this.gpuCompute.compute();

            // 計算した結果が格納されたテクスチャをレンダリング用のシェーダーに渡す

        }

        this.particleUniforms.texturePosition.value = this.gpuCompute.getCurrentRenderTarget( this.positionVariable ).texture;
        this.particleUniforms.textureVelocity.value = this.gpuCompute.getCurrentRenderTarget( this.velocityVariable ).texture;



        this.render();
    }



    public render() {


        this.renderer.render( this.scene, this.camera );
    }
}
