public BaseView getRecordFromDB(JSONObject tlsMap, ActionDetail actionDetail) {
       BaseView tlsView = new BaseView();
       SessionWrapper sessionWrapper = null;
       JSONObject record = new JSONObject();
       JSONObject relationObjects = new JSONObject();
       WAFTLSBean waftlsBean = null;
       try {
           sessionWrapper = new SessionWrapper(false);
           List beanlist = sessionWrapper.getReferenceList("WAFTLSBean", "", actionDetail);
           if (beanlist.size() > 0) {
               waftlsBean = (WAFTLSBean) beanlist.get(0);
               if (waftlsBean != null) {
                   record.put("id", waftlsBean.getId());
                   record.put("tls_v1_or_higher", waftlsBean.getTls_v1_or_higher());
                   record.put("tls_v1_1_or_higher", waftlsBean.getTls_v1_1_or_higher());
                   record.put("tls_v1_2_or_higher", waftlsBean.getTls_v1_2_or_higher());
               }
           }
           relationObjects.put("min_tls", sessionWrapper.getReferenceList("WAFTLSBean", "", actionDetail));
           tlsView.setRecord(record);
           tlsView.setRelationObjects(relationObjects);
       } catch (Exception e) {
           CyberoamLogger.error("WAFTLSHelper", "Error in getRecordFromDB", e);
       } finally {
           if (sessionWrapper != null) {
               sessionWrapper.close();
               sessionWrapper = null;
           }
       }
       return tlsView;
   }
