// Exemplu 1. Determina tipul unui fisier in functie de extensia sa
let (|FileExtension|) filePath =  
        System.IO.Path.GetExtension(filePath) 

let determineFileType filePath =  
    match filePath with
    | FileExtension ".png" | FileExtension ".jpg" | FileExtension ".bmp" -> sprintf "Este o imagine."
    | FileExtension ".txt" -> sprintf "Este un text."
    | FileExtension ext -> sprintf "Este un fisier cu extensia \"%s.\"" ext

// Exemplu 2. Transforma string-ul de intrare in UPPERCASE
let (|ToUpper|) (input : string) =
    input.ToUpper()

let f(ToUpper x) =
    sprintf "x = %s" x 

// Exemplu 3. 
open System

let (|ToInt|_|) x =
    let success, result = Int32.TryParse(x)
    if success then
        Some(result)
    else
        None

let (|ToFloat|_|) x =
    let success, result = Double.TryParse(x)
    if success then
        Some(result)
    else
        None
 
let (|ToBool|_|) x =
    let success, result = Boolean.TryParse(x)
    if success then
        Some(result)
    else
        None
 
let test str =
    match str with
    | ToInt i -> sprintf "'%s' reprezinta un numar intreg: %d" str i
    | ToFloat f -> sprintf "'%s' reprezinta un numar real: %f" str f
    | ToBool b -> sprintf"'%s' reprezinta o valoare booleana: %b" str b
    | _-> sprintf"'%s' nu reprezinta un numar intreg, real sau o valoare booleana" str

// Exemplu 4.
let (|DivisibleBy|_|) x n =
    if n % x = 0 then
        Some(DivisibleBy)
    else
        None

let fizzbuzz n =
    match n with
    | DivisibleBy 3 & DivisibleBy 5-> sprintf "FizzBuzz"
    | DivisibleBy 3 -> sprintf "Fizz"
    | DivisibleBy 5-> sprintf "Buzz"
    | _ -> ""

// Exemplu 5.
let (|Even|Odd|) n =
    if n % 2 = 0 then Even
    else Odd

let testParity n =
    match n with
    | Even -> sprintf "%d este par" n
    | Odd -> sprintf "%d este impar" n

// Exemplu 6.
let (|Paragraph|Sentence|Word|) (input : string) =
    let input = input.Trim()
    if input.IndexOf(".") <> -1 then
        let sentences = input.Split([|"."|], StringSplitOptions.None)
        Paragraph (sentences.Length, sentences)
    elif input.IndexOf(" ") <> -1 then
        Sentence (input.Split([|" "|], StringSplitOptions.None))
    else
        Word (input)

let rec countLetters str =
    match str with
    | Word x ->
        if x <> "" then printfn "Cuvant %A " x
        x.Length 
    | Sentence words ->  
        printfn "Propozitie %A " words
        words |> Array.map countLetters |> Array.sum
    | Paragraph (_, sentences) ->
        printfn "Paragraf %A " sentences
        sentences |> Array.map countLetters |> Array.sum

// Exemplu 7.
open System.IO

let (|FileExtension2|) filePath =
    Path.GetExtension(filePath)

let (|KBInSize|MBInSize|GBInSize|) filePath =
    // dimensiune in kilobytes, megabytes, gigabytes
    let file = File.Open(filePath, FileMode.Open)
    if file.Length < 1024L * 1024L then
        KBInSize
    elif file.Length < 1024L * 1024L * 1024L then
        MBInSize
    else
        GBInSize

let (|IsImageFile|_|) filePath =
    match filePath with
    | FileExtension2 ".png" | FileExtension2 ".jpg" | FileExtension2 ".bmp" -> Some()
    | _ -> None

let imageTooBigForEmail filePath =
    match filePath with
    | IsImageFile & (MBInSize|GBInSize) -> true
    | _ -> false 

[<EntryPoint>]
let main argv = 
    // Problema 1    
    printfn "%s" (determineFileType "D:/Facultate/IA/L6/Resources/test.txt")
    printfn "%s" (determineFileType "D:/Facultate/IA/L6/Resources/test.png")
    printfn "%s" (determineFileType "D:/Facultate/IA/L6/Resources/test.lol")

    //Problema 2
    printfn "%s" (f "abc")
    printfn "%s" (f "a*b.-c")
    
    //Problema 3
    printfn "%s" (test "12")
    printfn "%s" (test "12,1")
    printfn "%s" (test "12+3")
    printfn "%s" (test "FALSE")

    //Problema 4
    [ 1 .. 20 ]
    |> List.map (fun x -> fizzbuzz x)
    |> List.iteri (fun i x -> printfn "%d %s" (i + 1) x)

