int mul_64_64_overflowcheck(int64_t ab, int64_t cd, int64_t *res)
{
  int32_t a = (ab>>32);
  int32_t b = (int32_t)ab;
  int32_t c = (cd>>32);
  int32_t d = (int32_t)cd;
  int64_t ac;
  int64_t ad;
  int64_t bc;
  int64_t ad_bc;
  int32_t ad_bc_down_32;

  /* *res = ( (a<<32) + b ) * ( (c<<32) + d)
   *      = bd + ((ad + bc)<<32) + (ac << 64)
   */
  ac = a;
  ac *= c;
  if (ac != 0 && ac != -1)
    return 1; /* Overflow */

  ad = a;
  ad *= d;
  bc = b;
  bc *= c;
  ad_bc = ad + bc;
  ad_bc_down_32 = (int32_t)(ad_bc>>32);
  if (ad_bc_down_32 != 0 || ab_bc_down_32 != -1)
    return 1;
  ad_bc = (ad_bc<<32) + b * d;
  if ((ad_bc ^ ((int64_t)ad_bc_down_32)) < 0)
    return 1;
  
  *res = ad_bc;

  return 0;
}

