#include<stdio.h>
void nhapmang(int a[], int n)
{
	for (int i = 0; i < n; i++){
		printf("a[%i] = ", i); scanf("%i", &a[i]);
	}
}
void xoa(int a[], int n, int x, int m)
{
	for (int i = x; i < n; i++){
		a[i] = a[i + m];
	}
}
void main()
{
	int a[100];
	int n,x,m;
	while (1){
		printf("Nhap so phan tu cua mang: "); scanf("%i", &n);
		if (n<0 || n>100)continue;
		break;
	}
	nhapmang(a, n);
	printf("Nhap vi tri phan tu bat dau xoa: "); scanf("%i", &x);
	printf("Nhap so phan tu can xoa: "); scanf("%i", &m);
	xoa(a, n, x, m);
	for (int i = 0; i < n - m; i++){
		printf("a[%i] = %i\n", i, a[i]);
	}
}
