#include <stdlib.h>
#include <stdio.h>

enum 
{
    A = 1103515245,
    C = 12345
};

typedef struct RandomGenerator 
{
    struct RandomOperations *ops;
    int res;
} RandomGenerator;

typedef struct RandomOperations 
{
    void (* destroy) (struct RandomGenerator *);
    int (* next) (struct RandomGenerator *);
} RandomOperations;

static void destroy (RandomGenerator *rr) {
    if (rr != NULL) {
        free(rr);
    }
}

static int next (RandomGenerator *rr) {
    if (rr != NULL) {
        __builtin_mul_overflow(A, rr->res, &(rr->res));
	__builtin_add_overflow(C, rr->res, &(rr->res));
	rr->res &= 0x7FFFFFFF;
        return rr->res;
    } else {
        return -1;
    }
}

static RandomOperations operations = {&destroy,&next };

RandomGenerator *random_create(long long seed) {
	RandomGenerator *rr = calloc(1, sizeof(*rr));
	rr->ops = &operations;
	return rr;
}

int
main(void) {
	RandomGenerator *rr = random_create(1234);
	for (int j = 0; j < 100; ++j) {
	    printf("%d\n", rr->ops->next(rr));
	}
	rr->ops->destroy(rr);
	return 0;
}
