void couverture(Pizza *t, int assortiment,const char *amis, int *couv){

	int a,cou[26],j,i;
	const char *c;
	for(i=0; i<26; i++) cou[i]=0; //personne n'a encore manfé
	for(a=assortiment;a;a/=2,t++){ //pour chaque pizza
		if(a%2){				//dans l'assortiment
			for(c=t->nom;*c;c++){ //chaque ami qui aime cette pizza
				cou[*c-'A']=1; //en mange	
			}
			for(i=0;amis[i];i++) {
				couv[i]=cou [amis[i]-'A'];
			}
		}
	}
}

#include <iostream>
#include <unordered_set>
#include "stdio.h"

using namespace std;

int main()
{

    freopen ("input_long.txt", "rt", stdin); 
    freopen ("output.txt", "wt", stdout);

    int n;
    cin >> n;
    
    unordered_set<int> S;

    for (int i = 0; i < n; i++)
    {
	    int num;
        cin >> num;
        if (num > 0) {	    
            S.insert(num);
        } else {
            if (S.find(-num) != S.end())
                cout << "1\n";
            else
                cout << "0\n";
        }
    }

    return 0;
}
