printf("Podaj liczby z kwadratu: \n");
scanf("%d %d %d %d %d %d %d %d %d",&a1,&a2,&a3,&b1,&b2,&b3,&c1,&c2,&c3);

suma(a1,a2,a3);

int sumAw = suma(a1,a2,a3);
int sumBw = suma(b1,b2,b3);
int sumCw = suma(c1,c2,c3);

int sumAr = suma(a1,b1,c1);
int sumBr = suma(a2,b2,c2);
int sumCr = suma(a3,b3,c3);

int sumDIAG1 = suma(a1,b2,c3);
int sumDIAG2 = suma(a3,b2,c1);

if (sumAw==sumBw==sumCw==sumAr==sumBr==sumCr==sumDIAG1==sumDIAG2){
  printf("To jest kwadrat magiczny!\n");
  printf("Sumy wyrazow w wierszach, kolumnach i diagonalnych sa rowne: %d",sumAr);
}

else{
  printf("To nie jest kwadrat magiczny!\n");
}



}
