#include<fstream>
using namespace std;
ifstream f("conex.in");
ofstream g("conex.out");
int i,j,n,x,y,a[101][101],v[101],gasit;
void citire()
{
    f>>n;
    while(f>>x>>y)
        a[x][y]=a[y][x]=1;
}
void dfs(int nod)
{
    int i;
    v[nod]=1;
    
    for(i=1;i<=n;i++)
        if(a[nod][i]==1&&v[i]==0)
        dfs(i);
}
int main()
{
    citire();
    dfs(1);
    for(i=1;i<=n&&!gasit;i++)
        if(v[i]==0)
        gasit=1;
        if(gasit==0)
        g<<"DA";
        else
        
       g<<"NU";
        return 0;
}

