public class VibrationPatternAdapter extends RealmRecyclerViewAdapter<VibrationPattern,RecyclerView.ViewHolder> {

    private RecyclerVibrationPatternCallback callback;

    private static final int ITEM_TYPE_ITEM = 0;
    private static final int ITEM_TYPE_HEADER = 1;
    private Context context;

    public VibrationPatternAdapter(OrderedRealmCollection<VibrationPattern> data,Context context, @NotNull RecyclerVibrationPatternCallback callback) {
        super(data,true);
        this.context = context;
        this.callback = callback;
    }

    @Override
    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {
        if (viewType == ITEM_TYPE_HEADER) {
            return new VibrationPatternHeaderViewHolder(LayoutInflater.from(parent.getContext())
                    .inflate(R.layout.vibration_pattern_header, parent, false), context);
        }
        return new VibrationPatternViewHolder(LayoutInflater.from(parent.getContext())
                .inflate(R.layout.vibration_pattern_card, parent, false));
    }

    @Override
    public int getItemCount() {
        return super.getItemCount() + 1;
    }

    @Override
    public int getItemViewType(int position) {
        return position == 0 ? ITEM_TYPE_HEADER : ITEM_TYPE_ITEM;
    }

    @Nullable
    @Override
    public VibrationPattern getItem(int index) {
        return super.getItem(index - 1);
    }

    @Override
    public void onBindViewHolder(RecyclerView.ViewHolder abstractHolder, int position) {
        if (abstractHolder.getItemViewType() == ITEM_TYPE_ITEM){
            VibrationPatternViewHolder holder = (VibrationPatternViewHolder) abstractHolder;
            final VibrationPattern vibrationPattern = getItem((position));
            holder.titleTextView.setText(vibrationPattern.getName());
            holder.audioWaveView.setRawData(vibrationPattern.getPattern());
            holder.deleteButton.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    callback.delete(vibrationPattern);
                }
            });
            holder.tryButton.setOnClickListener(new View.OnClickListener() {
                @Override
                public void onClick(View v) {
                    callback.tryPattern(vibrationPattern);
                }
            });
        }
    }



    class VibrationPatternViewHolder extends RecyclerView.ViewHolder {
		....
    }

    class VibrationPatternHeaderViewHolder extends RecyclerView.ViewHolder implements OnProgressListener, SeekBar.OnSeekBarChangeListener{
		....
    }

    public interface RecyclerVibrationPatternCallback {
		....
    }


}
