let dfs1 (MNode(h, fst)) = 
		let rec aux acc fst = match fst with
			EmptyForest -> acc
			| Forest(MNode(v, succs), sibls) -> v :: aux (aux acc sibls) succs
	in h :: aux [] fst;;
	(* List.fold_right (@) (forest_map [[v]] dfs1 fst) [];; *)

let rec get_succs = function
	EmptyForest -> []
	| Forest(t, rest) -> t :: get_succs rest;;

let bfs1 (t : 'a mtree) =
		let rec aux vs = match vs with
			[] -> []
			| MNode(v, succs) :: tail -> v :: aux (tail @ (get_succs succs))
	in aux [t];;



let mtree1 = MNode(1, 
				Forest(MNode(2, Forest(MNode(3, Forest(MNode(4, EmptyForest), EmptyForest)), EmptyForest)), 
				Forest(MNode(5, Forest(MNode(6, Forest(MNode(7, EmptyForest), EmptyForest)), EmptyForest)),
				Forest(MNode(8, EmptyForest),
				EmptyForest
				))));;

print_int_list (dfs1 mtree1);;
print_int_list (bfs1 mtree1);;

type 'a mtree_lst = MTree of 'a * ('a mtree_lst) list;;

let dfs2 t = 
	let rec aux acc nodes = match nodes with
		[] -> acc
		| (MTree(v, succs)) :: sibls -> v :: aux (aux acc sibls) succs
	in aux [] [t];;

let bfs2 t = 
	let rec aux vs = match vs with
		[] -> []
		| (MTree(v, succs)) :: sibls -> v :: aux (sibls @ succs)
	in aux [t];;

let mtree2 = 
	MTree(1, [MTree(2, [MTree(3, [])]); MTree(4, [MTree(5, [])])]);;

print_int_list (dfs2 mtree2);; 
print_int_list (bfs2 mtree2);;

