using System.IO;

namespace _11._07_onallo_
{
    class Program
    {
        static void Main(string[] args)
        {
            StreamReader olvas = new StreamReader("ital.txt", Encoding.Default);
            StreamWriter iras = new StreamWriter("megoldas.txt", false, Encoding.Default);
            string[] inev = new string[10];
            int[] iar = new int[10];
            int[] ieladott = new int[10];
            string[] egysor = new string[3];
            int index = 0;
            int maxiar = 0;
            int maxieladott = 0;
            int osszeg = 0;
            int osszbev = 0;
            while (!olvas.EndOfStream)
            {
                egysor = olvas.ReadLine().Split(';');
                inev[index] = egysor[0];
                iar[index] = Convert.ToInt32(egysor[1]);
                ieladott[index] = Convert.ToInt32(egysor[2]);
                if (maxiar < iar[index])
                {
                    maxiar = iar[index];
                }
                if (maxieladott < ieladott[index])
                {
                    maxieladott = ieladott[index];
                }
                osszeg += ieladott[index];
                osszbev += iar[index] * ieladott[index];
                index++;
            }
            olvas.Close();
            iras.WriteLine("1, {0}", index);
            iras.WriteLine("2");
            for (int i = 0; i < index; i++)
			{
			    iras.WriteLine("A(z) {0} utáni bevétel {1} volt", inev[i], iar[i], ieladott[i]);
			}
            iras.WriteLine("Az összes bevétel {0} volt.", osszbev);
            iras.WriteLine("3, {0}", maxiar);
            iras.WriteLine("4, {0}", maxieladott);
            iras.WriteLine("5, {0}", osszeg);
            iras.Close();
        }
    }
