function sprawdzPakiety($klient, $taryfa) {
$starePakietySql = mysql_query("SELECT kp.id_pakiet
                        FROM 
                                    klient_pakiet AS kp
                        WHERE 
                                    id_klient = $klient
                        AND 
                                    data_wylaczenia >= now()");
$starePakiety = sprawdzLiczbeWierszy($starePakietySql);

$pakietyNowejTaryfySql = mysql_query("SELECT id_pakiet 
                        FROM 
                                    pakiet 
                        WHERE 
                                    id_abonament = $taryfa");
$nowePakiety = sprawdzLiczbeWierszy($pakietyNowejTaryfySql);

if(!$starePakiety)
    return "Brak starych pakietow";
else
    foreach($starePakiety as $sp) {
        if(in_array($sp, $nowePakiety))
            $pakietyDoDodania[] = $sp;
    }

return $pakietyDoDodania;
}
	
