import org.apache.spark.sql._
def getColType(df: DataFrame, colName: String):String = {
    df.dtypes.filter(_._1 == colName).map(_._2).head
}
def isApproximateSame(df: DataFrame, df1: DataFrame, cols: Seq[String], eps: Double):Column = {
    cols.map(colName =>{
        if(getColType(df, colName) == "DoubleType" && df(colName).isNotNull &&  df1(colName).isNotNull){
            abs(df(colName) - df1(colName)) < eps
        }else{
            df(colName) === df1(colName)
        }
    }).reduce(_ && _)
}
