import java.util.Scanner;

public class main {

    public static void main(String a[]) {

        StringBuilder sb = new StringBuilder();
        Scanner sc = new Scanner(System.in);
        int tc;
        tc=sc.nextInt();

        for (int j = 0; j < tc; j++) {

            String s = sc.nextLine();
            for (int i = 0; i < s.length(); i++) {
                char tmp = s.charAt(i);
                if (tmp == 'a' || tmp == 'e' || tmp == 'i' || tmp == 'o' || tmp == 'u')
                    continue;
                sb.append(tmp);
            }
            System.out.print(sb);
            System.out.print(j);
        }
    }
}
