// Shortcode
function industry_slider_shortcode( $atts, $content = null ) {

	$attr = shortcode_atts( array(
		'count'		=>	2,
		'type'	=>	'post'
	),$atts );

	extract( $attr );

	$arg = array(
		'post_type'			=>	'industry-slider',
		'posts_per_page'	=>	2
	);
	$get_post = new WP_Query( $arg );
	
	$slide_random_number = rand( 645789,836278 );

	$industry_slider_markup	 = '<div class="industry-slides">';

		$industry_slider_markup	.= '
			
			<script>
				jQuery(document).ready( function($) {
					$( "#industry-slider-'.$slide_random_number.'" ).owlCarousel({
						items: 1,
						loop: true,
						dots: true,
						nav: true,
						navText:true,
						navText: ["<i class=\'fa fa-angle-left\'></i>","<i class=\'fa fa-angle-right\'></i>"],
						autoplay: true,
			            animateIn: \'fadeIn\',
			            animateOut: \'fadeOut\'
					});
				} );
			</script>

			<div id="industry-slider-'.$slide_random_number.'" class="owl-carousel">';
				while ( $get_post->have_posts() ) : $get_post->the_post();
				$post_ID = get_the_ID();

				if ( get_post_meta( $post_ID, 'industy-slider-meta', true ) ) {
					$slider_meta = get_post_meta( $post_ID, 'industy-slider-meta', true );
				} else {
					$slider_meta = array();
				}

				if ( array_key_exists( 'text_color' , $slider_meta ) ) {
					$text_color = $slider_meta['text_color'];
				} else {
					$text_color = '#333';
				}

				if ( array_key_exists( 'enable_overlay' , $slider_meta ) ) {
					$enable_overlay = $slider_meta['enable_overlay'];
				} else {
					$enable_overlay = false;
				}

				if ( array_key_exists( 'overlay_color' , $slider_meta ) ) {
					$overlay_color = $slider_meta['overlay_color'];
				} else {
					$overlay_color = '#333';
				}

				if ( array_key_exists( 'overlay_opacity' , $slider_meta ) ) {
					$overlay_opacity = $slider_meta['overlay_opacity'];
				} else {
					$overlay_opacity = 70;
				}

				$industry_slider_markup	.= '
				<div style="background-image:url('.get_the_post_thumbnail_url( $post_ID, 'large' ).');" class="industry-single-item">';

					if ( $enable_overlay == true ) {
						$industry_slider_markup	.= '<div style="opacity:.'.$overlay_opacity.';background-color:'.$overlay_color.';" class="industry-slider-overlay">';
					}

					$industry_slider_markup	.= '
						<div class="d-table">
							<div class="d-table-cell">
								<div class="container">
									<div class="row">
										<div style="color:'.$text_color.';" class="col-md-6">
											<h2>'.get_the_title( $post_ID ).'</h2>
											'.wpautop( get_the_content( $post_ID ) ).'
										</div>
									</div>
								</div>
							</div>
						</div>';
					if ( $enable_overlay == true ) {
						$industry_slider_markup	.= '</div>';
					}

				$industry_slider_markup	.= '</div>';
				endwhile;
		$industry_slider_markup	.= '</div>';

	$industry_slider_markup	.= '</div>';

	wp_reset_query();

	return $industry_slider_markup;
	

}
add_shortcode( 'industry_slider', 'industry_slider_shortcode' );

