#include <avr/io.h>
#include <util/delay.h>
#include <util/setbaud.h>
#include <stdlib.h>
#include <math.h>

void initUSART(void) {
  UBRR0H = UBRRH_VALUE;
  UBRR0L = UBRRL_VALUE;
  #if USE_2X
  UCSR0A |= (1 << U2X0);
  #else
  UCSR0A &= ~(1 << U2X0);
  #endif

  UCSR0B = (1 << TXEN0) | (1 << RXEN0);
  UCSR0C = (1 << UCSZ01) | (1 << UCSZ00);  
}

void initTimer(void)
{
  TCCR1B = (1 << CS10);
}

void transmitByte(uint8_t data) {
  loop_until_bit_is_set(UCSR0A, UDRE0);
  UDR0 = data;
}
uint8_t receiveByte(void) {
  loop_until_bit_is_set(UCSR0A, RXC0);
  return UDR0;
}

void printString(const char myString[]) {
  uint8_t i = 0;
  while (myString[i]) {
    transmitByte(myString[i]);
    i++;    
  }
}

void printInt(uint16_t n)
{
  char str[10];
  itoa(n, str, 10);
  printString(str);
  printString("\n\r");
}

int main(void) {
  initUSART();
  initTimer();
  
  while(1)
  {
    uint8_t byte = receiveByte();
    printString("int types operations (8/16/32 bits)\n\r");

    volatile uint8_t int8 = 0x2F;
    volatile uint16_t int16 = 0x0102;
    volatile uint32_t int32 = 0x0A54;

    printString("Add:\n\r");
    TCNT1 = 0;
    int8 += byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int16 += byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int32 += byte;
    printInt(TCNT1);
    
    printString("Mult:\n\r");
    TCNT1 = 0;
    int8 *= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int16 *= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int32 *= byte;
    printInt(TCNT1);

    printString("Div:\n\r");
    TCNT1 = 0;
    int8 /= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int16 /= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int32 /= byte;
    printInt(TCNT1);

    printString("Modulo:\n\r");
    TCNT1 = 0;
    int8 %= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int16 %= byte;
    printInt(TCNT1);
    TCNT1 = 0;
    int32 %= byte;
    printInt(TCNT1);

    printString("Floating point types operations\n\r");

    volatile float f = 1.3436;
    volatile double d = 32.3435;
    volatile float auxF = f * byte;
    volatile double auxD = d * byte;

    printString("Add:\n\r");
    TCNT1 = 0;
    f += auxF;
    printInt(TCNT1);
    TCNT1 = 0;
    d += auxD;
    printInt(TCNT1);

    printString("Mult:\n\r");
    TCNT1 = 0;
    f *= auxF;
    printInt(TCNT1);
    TCNT1 = 0;
    d *= auxD;
    printInt(TCNT1);

    printString("Pow:\n\r");
    TCNT1 = 0;
    volatile float powF = pow(f, auxF);
    printInt(TCNT1);
    TCNT1 = 0;
    volatile float powD = pow(d, auxD);
    printInt(TCNT1);
    auxD += powD - powF;
  }

}
