char* couleur(char tab[]){
		//on récupère l'information transmise par le module bluetooth HC05
		init();

		for(int index=0;index<3;index++){
			char *sousChaine = strstr(tab,tabmot[index]);
			if(sousChaine != NULL){
				tabbool[index]=1;
			}
		}
		static char buffer[50];
		int size = 0;
		size += sprintf(buffer, "Les couleurs dans la chaine sont:");
		int size1=size;
		for(int i=0;i<=3;i++){
			if(tabbool[i]==1){
				size += sprintf(buffer+size, " %s",tabmot[i]);
			}

		}
		if(size==size1){
			return "Aucune couleur n'est presente";
		}else{
			return buffer;
		}
	}
