#include <Wire.h>
#include <LiquidCrystal_I2C.h>
LiquidCrystal_I2C lcd(0x3F ,2,1,0,4,5,6,7,3, POSITIVE);

volatile int NbTops; //measuring the rising edges of the signal
int Calc;                               
int flowsensor = 2;    //The pin location of the sensor

void setup(){
 pinMode(flowsensor, INPUT); //initializes digital pin 2 as an input
 Serial.begin(115200); //This is the setup function where the serial port is initialised,
 attachInterrupt(0, rpm, RISING); //and the interrupt is attached
 lcd.begin (16,2); //LCD untuk ukuran 16x2
 lcd.setCursor(0, 0); //baris pertama
 lcd.print("Nilai Debit");  
 }
 
void loop() {
  NbTops = 0;      //Set NbTops to 0 ready for calculations
  //sei();            //Enables interrupts
  delay (2000);      //Wait 1 second
  //cli();            //Disable interrupts
  Calc = (NbTops / 7.5 ); //(Pulse frequency) / 7.5Q, = flow rate in L/min
  //Serial.print (Calc); //Prints the number calculated above
  //Serial.print (" L/min\r\n"); //Prints "L/min" and returns a new line
  lcd.setCursor(0, 1); //baris kedua
  //lcd.print(Calc);   // print the Flow Rate
  lcd.print("L/min");
}

void rpm ()     //This is the function that the interupt calls 
{ 
  NbTops++;  //This function measures the rising and falling edge of the hall effect sensors signal
}

from __future__ import print_function
import os
from collections import defaultdict
import math


def tf_idf_classify(doc_names, query_words):
	docs = list()
	for doc in doc_names:
		docs.append(open(doc,'r').read().strip())

	tf_array = list()
	for doc_string in docs:
		term_freq = defaultdict(float)
		for word in doc_string.split():
			term_freq[word] += 1

		d = sum(term_freq.values())
		for k,v in term_freq.items():
			term_freq[k] = v / d

		tf_array.append(term_freq)

	idf = defaultdict(float)
	for term in query_words:
		idf[term] = 0.0

	for term in idf.keys():
		n = 0
		for tf in tf_array:
			if term in tf:
				n += 1
		idf[term] = 1 + math.log(len(doc_names) / n)

	query_res = defaultdict(list)

	for term in query_words:
		for tf in tf_array:
			if term in tf:
				query_res[term].append(tf[term] * idf[term])
			else:
				query_res[term].append(0.0)

	print('\n\n TF-IDF analysis: \n')
	title = '{:10}'.format('Query') + ''.join(['| {:10}'.format(doc) for doc in doc_names])
	print(title)
	print('-----------' + '+-----------' * len(doc_names) + '+')
	for result in query_res.keys():
		print('{:10}'.format(result))
		print(''.join(['| {:10} '.format(str(i)[:8]) for i in query_res[result]]) + '|')

def main():
	num_doc = int(input('Enter number of documents: '))
	print('Enter paths of documents')
	doc_names = [input().strip() for i in range(num_doc)]

	for doc in doc_names:
		if not os.path.exists(doc):
			print('{} does not exist. Exiting'.format(doc))
			quit()

	query_words = input('Enter query strings: ').split()
	tf_idf_classify(doc_names, query_words)


if __name__ == '__main__':
	main()
