import React from 'react';
import ReactDOM from 'react-dom';

document.addEventListener('DOMContentLoaded', function(){

    function SumFn(props) {
        return <h1>{props.numbers.reduce((prev, curr) => prev + curr)}</h1>
    }

    class SumCl extends React.Component {
        render() {
            return <h1>{this.props.numbers.reduce((prev, curr) => prev + curr)}</h1>
        }
    }
 
    ReactDOM.render(
        <div>
            <SumFn numbers={ [1,2,3] } />
            <SumCl numbers={ [1,2,3, 4] } />
        </div>,
        document.getElementById('app')
    );
});
