package com.swinarski.lab04swinarski;

import android.content.ContentValues;
import android.database.sqlite.SQLiteDatabase;
import android.support.v7.app.AppCompatActivity;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.CheckBox;
import android.widget.EditText;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    private Button loginButton;

    private EditText login;

    private EditText password;

    private CheckBox rememberMeCheckbox;

    DatabaseOpenHelper mDbHelper;

    SQLiteDatabase mDB;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        mDbHelper = new DatabaseOpenHelper(this);

        mDB = mDbHelper.getWritableDatabase();

        login = (EditText) findViewById(R.id.login);

        password = (EditText) findViewById(R.id.password);

        loginButton = (Button) findViewById(R.id.loginButton);

        rememberMeCheckbox = (CheckBox) findViewById(R.id.rememberMeCheckbox);

        loginButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                String loginText = login.getText().toString();

                String passwordText = password.getText().toString();

                if (loginText.isEmpty()) {
                    login.setError(getString(R.string.login_empty_error));

                    return;
                }
                else if (loginText.length() < 5) {
                    login.setError(getString(R.string.login_range_error));

                    return;
                }

                if (passwordText.isEmpty()) {
                    password.setError(getString(R.string.password_empty_error));

                    return;
                }
                else if (passwordText.length() < 8) {
                    password.setError(getString(R.string.password_range_error));

                    return;
                }

                if (rememberMeCheckbox.isChecked()) {
                    ContentValues values = new ContentValues();
                    values.put(DatabaseOpenHelper.USER_LOGIN, loginText);
                    values.put(DatabaseOpenHelper.USER_PASSWORD, passwordText);

                    mDB.insert(DatabaseOpenHelper.TABLE_NAME, null , values);
                }
            }
        });

        List<User> debtors = new ArrayList<>();

        Cursor cursor = getReadableDatabase().query(TABLE_NAME, null, null, null, null, null, _ID);

        cursor.moveToFirst();
        while (!cursor.isAfterLast()) {
            Debtor debtor = new Debtor();

            int idx = cursor.getColumnIndex(DEBTOR_NAME);
            debtor.setName(cursor.getString(idx));

            idx = cursor.getColumnIndex(DEBTOR_PHONE);
            debtor.setPhone(cursor.getString(idx));

            idx = cursor.getColumnIndex(DEBTOR_PHOTO_URI);
            debtor.setAvatar(cursor.getString(idx));

            idx = cursor.getColumnIndex(_ID);
            debtor.setId(cursor.getString(idx));

            debtors.add(debtor);

            cursor.moveToNext();
        }

        cursor.close();
    }
}

@BrianOCD Last time I tapered off Cymbalta my pdoc did the taper using Prozac. Prozac effects Serotonin and has a very long half-life, so it facilitates the transition coming off other SSRIs or SNRI's. I can't recall the exact taper schedule, but I had first weaned down as far as I could stand on Cymbalta, and then we added small dose Prozac to slowly continue off. I still had some withdrawal symptoms, but they were less debilitating.
How long have you been on Effexor btw? Has it worked well for you? Will you start on something else?
