#include<iostream>
#include<math.h>
using namespace std;

long int cekprim (long int a){
	int temp;
	temp=0;
		for (int j=2;j<=sqrt(a);j++){ //CUKUP SAMPE AKAR A (TEORI BILANGAN)
			if (a%j==0){
				temp=1;
				break;
			};
		};
	if (temp==1){
		return 1;
	} else return 0;
}
int main (){
	long int testcase;
	cin >> testcase; 	// INPUT TESTCASE
	long int x[testcase],y[testcase];
	for (int i=1;i<=testcase;i++){
		cin >> x[i] >> y[i]; //INPUT BILANGAN X DAN Y
	};

	for (int i=1;i<=testcase;i++){
		if (x[i] > 1 || y[i] > x[i] || y[i] > 10000 || y[i]-x[i] > 1000){
			cout << "ERROR" << endl; //JIKA ERROR
		} else
		for (int j=x[i];j<=y[i];j++){
			if (j==1){continue;}
			if (cekprim(j)==0){
				cout << j << endl; //JIKA PRIMA MAKA DIKELUARKAN
			};
		};
	cout << endl;
	};
}
