#include <Wire.h>
#include <LiquidCrystal_I2C.h>
LiquidCrystal_I2C lcd(0x3F ,2,1,0,4,5,6,7,3, POSITIVE);
 
volatile int NbTops; //measuring the rising edges of the signal
int Calc;                              
int flowsensor = 2;    //The pin location of the sensor
 
void setup(){
 pinMode(flowsensor, INPUT); //initializes digital pin 2 as an input
 Serial.begin(115200); //This is the setup function where the serial port is initialised,
 attachInterrupt(0, rpm, RISING); //and the interrupt is attached
 lcd.begin (16,2); //LCD untuk ukuran 16x2
 lcd.setCursor(0, 0); //baris pertama
 lcd.print("Nilai Debit");  
 }
 
void loop() {
  NbTops = 0;      //Set NbTops to 0 ready for calculations
  //sei();            //Enables interrupts
  delay (2000);      //Wait 1 second
  //cli();            //Disable interrupts
  Calc = (NbTops / 7.5 ); //(Pulse frequency) / 7.5Q, = flow rate in L/min
  //Serial.print (Calc); //Prints the number calculated above
  //Serial.print (" L/min\r\n"); //Prints "L/min" and returns a new line
  lcd.setCursor(0, 1); //baris kedua
  //lcd.print(Calc);   // print the Flow Rate
  lcd.print("L/min");
}
 
void rpm ()     //This is the function that the interupt calls
{
  NbTops++;  //This function measures the rising and falling edge of the hall effect sensors signal
}


