	out << "Automobil electric";
	out << "\n\tMarca: " << marca;
	out << "\n\tModel: " << model;
	out << "\n\tTip baterie: " << baterie;
	out << "\n\tPutere: " << putere << " cp.";
	out << "\n\tAutonomie: " << autonomie << " km.";
	out << "\n\tPret: " << pret << " EUR";
	out << "\n======================================\n";
}
Automobil* AutomobilElectric::citesteDate() {
	char *marca = new char[20];
	char *model = new char[20];
	char *baterie = new char[10];
	int putere, pret, autonomie;
	cout << "\nAdaugare automobil electric:";
	cout << "\n\tMarca:"; cin >> marca;
	cout << "\n\tModel:"; cin >> model;
	cout << "\n\tPutere:"; cin >> putere;
	cout << "\n\tBaterie:"; cin >> baterie;
	cout << "\n\tAutonomie (km.):"; cin >> autonomie;
	cout << "\n\tPret (EUR):"; cin >> pret;
	return new AutomobilElectric(marca, model, putere, pret, baterie, autonomie);
}

#include "AutomobilHibrid.h"
AutomobilHibrid::AutomobilHibrid(char* marca, char* model, int putereAI, int putereEl, int
	cc, char* combustibil, double consum, int pret, char* baterie, int
	autonomie) :Automobil(marca, model, pret), AutomobilArdereInterna(marca, model, putereAI, pret,
		cc, combustibil, consum), AutomobilElectric(marca, model, putereEl, pret, baterie, autonomie) {}
AutomobilHibrid::~AutomobilHibrid() {}
void AutomobilHibrid::scrieInformatii(ostream& out) {
	out << "Automobil hibrid";
	out << "\n\tMarca: " << marca;
	out << "\n\tMarca: " << model;
	out << "\n\tCapacitate cilindrica: " << cc << " cc.";
	out << "\n\tTip combustibil: " << combustibil;
	out << "\n\tTip baterie: " << baterie;
	out << "\n\tPutere: ";
	out << "\n\t\tMotor ardere interna: " << AutomobilArdereInterna::putere << " cp.";
	out << "\n\t\tMotor electric: " << AutomobilElectric::putere << " cp.";
	out << "\n\t\tTotal: " << AutomobilArdereInterna::putere + AutomobilElectric::putere << "cp.";
	out << "\n\tConsum: " << consum << " litri/100 km.";
	out << "\n\tPret: " << pret << " EUR";
	out << "\n======================================\n";
}
Automobil* AutomobilHibrid::citesteDate() {
	char *marca = new char[20];
	char *model = new char[20];
	char *baterie = new char[10];
	char *combustibil = new char[10];
	int putereAI, putereEl, pret, autonomie, cc;
	double consum;
	cout << "\nAdaugare automobil hibrid:";
	cout << "\n\tMarca:"; cin >> marca;
	cout << "\n\tModel:"; cin >> model;
	cout << "\n\tCapacitate cilindrica (cc.):"; cin >> cc;
	cout << "\n\tPutere motor ardere interna:"; cin >> putereAI;
	cout << "\n\tCombustibil:"; cin >> combustibil;
	cout << "\n\tConsum (litri/100 km.):"; cin >> consum;
	cout << "\n\tPutere motor electric:"; cin >> putereEl;
	cout << "\n\tBaterie:"; cin >> baterie;
	cout << "\n\tAutonomie (km.):"; cin >> autonomie;
	cout << "\n\tPret (EUR):"; cin >> pret;
	return new
		AutomobilHibrid(marca, model, putereAI, putereEl, cc, combustibil, consum, pret, baterie,
			autonomie);
}

#include <iostream>
#include "Manager.h"
using namespace std;
Manager::Manager() {
	numarAutomobile = 0;
}
Manager::~Manager() {
	for (int i = 0; i<numarAutomobile; i++) {
		delete listaAutomobile[i];
	}
}
void Manager::afiseazaAutomobile() {
	if (numarAutomobile == 0) {
		cout << "\nNu exista automobile in lista.";
		return;
	}
	for (int i = 0; i<numarAutomobile; i++)
	{
		listaAutomobile[i]->scrieInformatii(cout);
	}
}
void Manager::scrieAutomobileInFisier() {
	ofstream out("automobile.txt"); //deschidem fisierul pentru scriere
	if (out.good()) {
		for (int i = 0; i<numarAutomobile; i++)
		{
			listaAutomobile[i]->scrieInformatii(out);
		}
		cout << "\n\nLista automobilelor a fost salvata in fisierul\"automobile.txt\"" << endl;
	}
	else
		cout << endl << "!!!Eroare la salvare!!!" << endl;
}
void Manager::adaugaAutomobil() {
	int raspuns;
	cout << "\nAlegeti tipul autoturismului pe care doriti sa-l adaugati:";
	cout << "\n\t1) Automobil cu ardere interna";
	cout << "\n\t2) Automobil electric";
	cout << "\n\t3) Automobil hibrid";
	cout << "\n\n-->";
	cin >> raspuns;
	Automobil* automobil = 0;
	if (raspuns == 1)
		automobil = AutomobilArdereInterna::citesteDate();
	else if (raspuns == 2)
		automobil = AutomobilElectric::citesteDate();
	else if (raspuns == 3)
		automobil = AutomobilHibrid::citesteDate();
	if (automobil != 0) {
		listaAutomobile[numarAutomobile] = automobil;
		if (numarAutomobile<numarMaximAutomobile)numarAutomobile++;
	}
}
void Manager::start() {
	int raspuns;
	do {
		cout << "\nAlegeti operatia dorita:";
		cout << "\n\t1) Adaugare automobil";
		cout << "\n\t2) Afisare lista automobile";
		cout << "\n\t3) Scriere lista automobile in fisier";
		cout << "\n\t4) Iesire";
		cout << "\n\n-->";
		cin >> raspuns;
		if (raspuns == 1)
			adaugaAutomobil();
		else if (raspuns == 2)
			afiseazaAutomobile();
		else if (raspuns == 3)
			scrieAutomobileInFisier();
	} while (raspuns != 4);
}

#include "Manager.h"
void main() {
	Manager *manag = new Manager();
	manag->start();
	delete manag;
}
