#include "stdafx.h"
#include <iostream>
#include <vector> //korzystanie z wektorów
using namespace std;



void wyswietl(vector<int>liczby)
{
	for (int i = 0; i <liczby.size(); i++)
	{
		cout << liczby[i] << " ";
	}
	cout << endl;

}

int suma(vector<int>liczby)
{
	int wynik = 0;
	for (int i = 0; i < liczby.size(); i++)
	{
		wynik += liczby[i];
	}
	return wynik;



}
float srednia(vector<int>liczby)
{
	int wynik = suma(liczby);
	return (float)wynik / liczby.size();

}

int min(vector<int>liczby)
{
	int wynik = liczby[0];

	for (int i = 1; i < liczby.size(); i++)
	{
		if (liczby[i] < wynik)
		{
			wynik = liczby[i];
		}
	}
	return wynik;
}
int max(vector<int>liczby)
{
	int wynik = liczby[0];

	for (int i = 1; i < liczby.size(); i++)
	{
		if (liczby[i] > wynik)
		{
			wynik = liczby[i];
		}
	}
	return wynik;
}

bool czyNalezy(vector<int>liczby, int wartosc)
{
	for (int i = 0; i < liczby.size(); i++)
	{
		if (liczby[i] == wartosc)
		{
			return true;
		}
	}
	return false;

}


int _tmain(int argc, _TCHAR* argv[])
{
	vector <int> liczby;
	liczby.push_back(9);
	liczby.push_back(8);
	liczby.push_back(1);
	liczby.push_back(3);
	liczby.push_back(5);

	wyswietl(liczby);
	wyswietl(liczby);

	int wynik = suma(liczby);
	cout << wynik << endl;



	cout << liczby.size() << endl;
	cout << liczby[0] << endl;

	float x=srednia(liczby);
	cout << x << endl;

	int y= min(liczby);
	cout << y << endl;

	int z = max(liczby);
	cout << z << endl;
	
	bool wartosc=czyNalezy(liczby, 4);
	cout << wartosc << endl;


	system("pause");
	return 0;
}
