#include<iostream>
#include<fstream>

using namespace std;

ifstream fin("date.in");
ofstream fout("date.out");

void Sortare(int A[], int n);
int pozitie(int x);

int n,Ai[1005],A[1005];
///Ai->sirul initial, A->sirul sortat
int s;///timpul in care gigel poate sa cante


int main()
{
    fin>>n>>s;
    for(int i=1;i<=n;i++)
    {
        fin>>Ai[i];
        A[i]=Ai[i];///copiem in A elementele din Ai (sirul initial)
    }

    Sortare(A,n);///sortam sirul A

    fout<<A[1]<<endl;///prima cerinta


    int P[1005],c=0;///P-> vector de pozitii, c-> numarul de elemente din P

    for(int i=1;i<=n;i++)
        if(Ai[i]==A[n])///cautam in sirul initial toate elementele care sunt = cu maximul
            P[++c]=i;///salvam pozitiile unde gasim elemente egale cu maximul

    ///a doua certinta
    fout<<c<<endl;
    for(int i=1;i<=c;i++)
        fout<<P[i]<<' ';
    fout<<endl;


    int sp;
    sp=0;
    c=0;
    
    ///luam elemente din sir cat timp numar de secunde pana acum nu depaste numarul max de secunde
    for(int i=1;i<=n && sp<s;i++)
        if(sp+A[i]<=s)
        {
            P[++c]=pozitie(A[i]);
            sp=sp+A[i];
        }

    Sortare(P,c);///sortam P

    fout<<endl;
    fout<<c<<endl;
    for(int i=1;i<=c;i++)
        fout<<P[i]<<' ';

    fin.close();
    fout.close();
    return 0;
}

int pozitie(int x)
{
    int poz;

    for(int i=1;i<=n;i++)
        if(Ai[i]==x)
        {
            poz=i;
            break;
        }

    Ai[poz]=-1;

    return poz;
}

void Sortare(int A[], int n)
{
    for(int i=1;i<n;i++)
        for(int j=i+1;j<=n;j++)
            if(A[i]>A[j])
            {
                int aux;
                aux=A[i];
                A[i]=A[j];
                A[j]=aux;
            }
}
