#include <cstdlib>
#include <cmath>
#include <iostream>
 
using namespace std;
 
int main()
{
    int n;
    cin >> n;
    int count = pow (2, n);
     
    string table[count];
    int f[count], mas[count], arr[count], ans[count];
     
    for (int i = 0; i < count; i++)
        cin >> table[i] >> f[i];
     
    for (int i = 0; i < count; i++)
    {
        mas[i] = f[i];
        arr[i] = mas[i];
    }
     
    int k = 0;
    for (int j = 0; j < count; j++)
    {
        for (int i = 0; i < count - k; i++)
        {
            if (k % 2 == 0)
            {
                mas[i] = arr[i] ^ arr[i + 1];
                ans[j] = arr[0];
            }
            else
            {
                arr[i] = mas[i] ^ mas[i + 1];
                ans[j] = mas[0];
            }
        }
        k++;
    }
     
    for (int i = 0; i < count; i++)
        cout << table[i] << " " << ans[i] << endl;
     
    return 0;
}
