#include <stdio.h>
#include <stdlib.h>


/*controlla il contenuto del file prime.txt, se non esiste scrive 1 ed esce, se esiste, crea un figlio che legge l'intero dentro il file, e cercaq il num orimo successivo*/

int isPrime(int n){
	int i;
	int flag = 0;
	for(i = 2; i <= n/2; ++i) {
		if(n % i == 0){
			flag = 1;
			return flag;
		}
	}
	return flag;

}

void createFile() {
	FILE*fp = fopen("prime.txt", "r");
}

int readFile() {
		FILE *fp = fopen("/home/dorizza/Desktop/prime.txt", "r");
		int num; 
		fscanf(fp, "%d", &num);
		fclose(fp);
	return num;
}

void writeFile(int n) {
	  FILE *f = fopen("/home/dorizza/Desktop/prime.txt", "w");
		if (f == NULL) {
				printf("Error opening file\n");
				exit(1);
		}
		fprintf(f, "%d\n", n);
		fclose(f);
}

int main(int argc, char *argv[]) {

		//padre	
		FILE *f = fopen("/home/dorizza/Desktop/prime.txt", "r");
		if (f == NULL) {
			createFile();
			writeFile(1);
			return 0;
		}
		else{
			int myPid;
			myPid = fork();
			wait();
			if(myPid == 0) {
				//figlio
				int num = readFile();
				num++;
				while(isPrime(num) == 1) {
					num++;
				}
				writeFile(num);
				exit(1);
			}
			exit(1);
		}
}
