using namespace std;

struct nod{
    int info;
    nod *urm;
};

void adaugare(nod* &p, int x)
{
                            /// Alocare si initializare nod
     nod *elem = new nod;
     elem->info = x;
     elem->urm = NULL;
                            /// daca avem lista vida
    if (p == NULL)
                            /// doar modificam capul listei
        p = elem;
    else
     {
     /// parcurgem lista pana la ultimul nod
     nod *q = p;
     while (q->urm != NULL)
     q = q->urm;
     /// adaugam nodul nou in lista
     q->urm = elem;
     }
}

void afisare(nod * p)
{
        while(p!=NULL){
            cout<<p->info<<" ";
            p=p->urm;
        }
}
///sterge elementul q din lista
void sterge(nod * &p, nod * q)
{
    nod *r,*elem;
    if(p==q){
        p=p->urm;
        delete q;
        return;
    }
    elem=p;
    while(elem!=q){
        r=elem;
        elem=elem->urm;
    }
    r->urm = q->urm;
    delete q;
}

///sterge toate valorile pare din lista
void sterge2(nod * & p)
{
    nod *elem,*s,*r;
    elem=p->urm; ///curent
    r=p; ///anterior
    while(elem!=NULL){
        if (elem->info%2==0){
            r->urm=elem->urm;
            s=elem;
            elem=elem->urm;
            delete s;
        }
        else{
            r=elem;
            elem=elem->urm;
        }
    }
    elem=p;
    if(elem->info%2==0){
        p=p->urm;
        delete elem;
    }
}
int main()
{
    int n=10;
    nod *prim=NULL, *p;
    for(int i=2;i<=n;i++){
        adaugare(prim,i);
    }
    p=prim->urm;
    sterge2(prim);
    afisare(prim);
    return 0;

}
