#include <iostream>
#include <exception>
#include <cmath>
#include <fstream>
#include <string>
#define E 2.71828182845904523536
 
class Vector
{
    double*     vector;
    int         size;
public:
    Vector(int n)
    {
        size = n;
        vector = new double[size];
    }
    Vector(Vector & v)
    {
        size = v.size;
        vector = new double[size];
        for(int i = 0; i < size; i++)
        {
            vector[i] = v[i];
        }  
    }
    double& operator [] (int i)
    {
        if (i >= size || i < 0)
            throw "Incorrect index in vector!\n";
        return vector[i];
    }
    void writeIntoFile(char* s)
    {
        std::ofstream fout;
        fout.open(s);
        fout << "x, y\n";
        for (int i = 0; i < size; i++)
        {
            fout << (double)i / (size - 1) << ',' << vector[i] << std::endl;
        }
    }
    void initialize_u(double h, int N, double eps)
    {
        vector[0] = 1.0 - h / 2.0;
        vector[N] = h / 2.0 - 2.0 / eps;
        for(int i = 1; i < N; i++)
        {
            vector[i] = 1.0 / eps;
        }
    }
    Vector* countDif(double h, double g_v, double g_u, double eps)
    {
        Vector* res = new Vector(size);
        (*res)[size-1] = g_v;
        for(int i = size-2; i > 0; i--)
        {
            (*res)[i] = (vector[i + 1] - vector[i - 1]) / (h * 2.0);
            (*res)[i] *= -eps;
            (*res)[i] -= 1.0;
        }
        (*res)[0] = -1.0 - eps * g_u;
 
        return res;
    }
    int getLength()
    {
        return size;
    }
    ~Vector()
    {
        delete[] vector;
    }
};
 
class Matrix
{
    double**    matrix;
    int         nrow, ncolumn;
public:
    Matrix(int row, int column)
    {
        nrow = row;
        ncolumn = column;
        matrix = new double* [nrow];
        for(int i = 0; i < nrow; i++)
        {
            matrix[i] = new double [ncolumn];
        }
    }
    double* operator[] (int i)
    {
        if (i < 0 || i >= nrow)
            throw "Incorrect index in matrix!\n";
        return matrix[i];
    }
    void swapRows(int i, int j)
    {
        Vector v(ncolumn);
        for(int p = 0; p < ncolumn; p++)
        {
            v[p] = matrix[i][p];
        }
        for(int p = 0; p < ncolumn; p++)
        {
            matrix[i][p] = matrix[j][p];
        }
        for(int p = 0; p < ncolumn; p++)
        {
            matrix[j][p] = v[p];
        }
    }
    Vector* tridiagonalAlgorythm (Vector b)
    {
        if(nrow != ncolumn)
            throw "Method isn't possible for non-square matrix";
        for (int i = 1; i < nrow; i++)
        {
            matrix[i][i] -= matrix[i][i - 1] / matrix[i - 1][i - 1] * matrix[i - 1][i];
            b[i] -= matrix[i][i - 1] / matrix[i - 1][i - 1] * b[i - 1];
        }
        Vector* res = new Vector(nrow);
        (*res)[nrow - 1] = b[nrow - 1] / matrix[nrow - 1][nrow - 1];
        for(int i = nrow - 2; i >= 0; i--)
        {
            (*res)[i] = (b[i] - matrix[i][i + 1] * (*res)[i + 1]) / matrix[i][i];
        }
        return res;
    }
    void initialize_u(double h, int N, double eps)
    {
        for(int i = 1; i < N; i++)
        {
            matrix[i][i + 1] = - 1.0 / (h * h);
            matrix[i][i - 1] = - 1.0 / (h * h);
        }
        matrix[N][N - 1] = - 1.0 / h;
        matrix[0][1] =  1.0 / h;
        for(int i = 1; i < N; i++)
        {
            matrix[i][i] = 2.0 / (h * h) - pow(E, i * h) / eps;
        }
        matrix[0][0] = - 1.0 / h + h / (2.0 * eps);
        matrix[N][N] = 1.0 / h - h * E / (2.0 * eps);
 
    }
    ~Matrix()
    {
        for (int i = 0; i < nrow; i++)
        {
            delete[] matrix[i];
        }
        delete[] matrix;
    }
};
 
 
int main()
{
    try{
 
        int N;
        double h;
        double eps;
        std::cout << "Input N, please:" << std::endl;
        std::cin >> N;
        std::cout << "Input epsilon, please:" << std::endl;
        std::cin >> eps;
        h = 1.0 / N;
        Matrix matrix(N + 1, N + 1);
        matrix.initialize_u(h, N, eps);
        Vector F(N + 1);
        F.initialize_u(h, N, eps);
        
        Vector* x = matrix.tridiagonalAlgorythm(F);
        x->writeIntoFile("f1.txt"); // x contains u(x)
        Vector* p = x->countDif(h, 1.0, 1.0, eps);
        p->writeIntoFile("f2.txt"); //p contains v(x)
 
        std::ofstream fout; // curve (u(x), v(x))
        fout.open("out3.txt");
        fout << "x, y\n";
        double size = p->getLength();
        for (int i = 0; i < size; i++)
        {
            fout << (*x)[i] << ',' << (*p)[i] << std::endl;
        }
    }
    catch(const char* c)
    {
        std::cout << c;
    }
}

