public class RestaurantInterface
{
	public static void main(String args[])
	{
		ServerSocket serverSocket = null;
		Socket socket = null;
		int portNum = 7777;
		OrderForm orderInfo;



		// make server socket and bind port #portNum
		try
		{
			serverSocket = new ServerSocket(portNum);
			System.out.println("Server is ready.");
		}
		catch(IOException ioe)
		{
			ioe.printStackTrace();
		}



		// show GUI
		FrameManager f = new FrameManager();
		Frame mainFrame = f.getFrame("Mat's Restaurant");



		// wait client request
		while(true) // after execute process, server wait for client request
		{
			try
			{ // if request is recieved, make new socket for communicating with client
//				System.out.println(getTime() + " Waiting connection request...");
				System.out.println("Waiting connection request...");
				socket = serverSocket.accept();		//접속허용

//				System.out.println(getTime() + " Connection requset from " + socket.getInetAddress());	//접속시간, 접속한 사람 IP
				System.out.println("Connection requset from " + socket.getInetAddress());

				// read output stream of socket
				OutputStream out = socket.getOutputStream();
				DataOutputStream dos = new DataOutputStream(out);

				// make message sender and receiver instance
				MsgSender sender = new MsgSender(socket);
				MsgRcver rcver = new MsgRcver(socket);

				// start thread
				sender.start();
				rcver.start();

				// send data to socket
				dos.writeUTF("[Notice] Admit connection.");
//				System.out.println(getTime() + " Data is sended.");
				System.out.println("Admit connection message passed.");

				// close stream and socket
				dos.close();
				socket.close();
			}catch(IOException ioe){
				ioe.printStackTrace();
			}
		} // while loop
	} // method main()





