class MsgSender extends Thread
{
	// --- member field
	Socket socket;
	DataOutputStream out;
	String name;


	// --- constructor
	MsgSender(Socket socket)
	{
		this.socket = socket;
		try
		{
			out = new DataOutputStream(socket.getOutputStream());
			name = "[" + socket.getInetAddress() + ":" + socket.getPort() + "]";
		}catch(Exception e){}
	} // constructor



	public void run()
	{
		Scanner scan = new Scanner(System.in);
		while(out != null)
		{
			try
			{
				out.writeUTF(name + scan.nextLine());
			}catch(IOException e){}
		} // while loop
	} // method run()
} // class MsgSender





class MsgRcver extends Thread
{
	Socket socket;
	DataInputStream in;


	// constructor
	MsgRcver(Socket socket)
	{
		this.socket = socket;
		try
		{
			in = new DataInputStream(socket.getInputStream());
		}catch(IOException e){}
	} // constructor



	public void run()
	{
		while(in != null)
		{
			try
			{
				System.out.println(in.readUTF());
			}catch(IOException e){}
		} // while loop
	} // method run()
} // class MsgRcvcer
