package map;

import map.SimpleHashMap.Entry;

public class SimpleHashMap<K, V> implements Map<K, V> {
	private Entry<K, V>[] table;
	private int size;
	private static final double loadFactor = 0.75;
	private int capacity;

	public static class Entry<K, V> implements Map.Entry<K, V> {
		private K key;
		private V value;
		private Entry<K, V> next;

		public Entry(K key, V value) {
			this.key = key;
			this.value = value;
		}

		@Override
		public K getKey() {
			return key;
		}

		@Override
		public V getValue() {
			return value;
		}

		@Override
		public V setValue(V value) {
			this.value = value;
			return this.value;
		}

		@Override
		public String toString() {
			return getKey().toString() + "=" + getValue().toString();

		}

	}

	public SimpleHashMap() {
		size = 0;
		table = (Entry<K, V>[]) new Entry[16];
		capacity = 16;
	}

	public SimpleHashMap(int capacity) {
		size = 0;
		table = (Entry<K, V>[]) new Entry[capacity];
		this.capacity = capacity;
	}

	private int index(K key) {
		int index = key.hashCode() % capacity;
		index = Math.abs(index);
		return index;
	}

	private Entry<K, V> find(int index, K key) {
		Entry<K, V> e = table[index];
		while (e != null) {
			if (e.key.equals(key)) {
				return e;
			}
			e = e.next;
		}
		return null;
	}

	 /**
	 * Varför blir det bäst resultat när man
	 * multiplicerar med 10?
	 */
	private void rehash() {
		capacity = capacity * 10;
		Entry<K, V>[] oldArray = table;
		table = (Entry<K, V>[]) new Entry[capacity];
		size = 0;
		for (int i = 0; i < oldArray.length; i++) {
			Entry<K, V> head = oldArray[i];
			while (head != null) {
				put(head.key, head.value);
				head = head.next;
			}

		}
	}

	@Override
	public V get(Object object) {
		K key = (K) object;
		int index = index(key);
		Entry<K, V> e = find(index, key);
		if (e != null) {
			return e.value;
		}
		return null;
	}

	@Override
	public boolean isEmpty() {
		return size == 0;
	}

	public V put(K key, V value) {
		if (((double) size / capacity) > loadFactor) {
			rehash();
		}
		int index = index(key);
		Entry<K, V> e = find(index, key);
		if (e != null) {
			V oldValue = e.getValue();
			e.setValue(value);
			return oldValue;
		} else {
			Entry<K, V> newEntry = new Entry<K, V>(key, value);
			// Om det finns lista i table[index]
			if (table[index] != null) {
				newEntry.next = table[index];
				table[index] = newEntry;
			} else {
				table[index] = newEntry;
			}

			size++;
			return null;
		}

	}

	@Override
	public V remove(Object arg0) {
		if (isEmpty()== false) {
			K key = (K) arg0;
			int index = index(key);
			Entry<K, V> NodeToRemove = find(index, key);
			if (NodeToRemove != null) {
				Entry<K, V> entry = table[index];
				// Om f�rsta objektet i listan
				if (entry.key.equals(key)) {
					table[index] = entry.next;
					size--;
					return entry.value;
				}
				while (entry.next != null) {
					if (entry.next.key.equals(key)) {
						Entry<K, V> temp = entry.next;
						entry.next = entry.next.next;
						size--;
						return temp.value;
					}
					entry = entry.next;
				}
			}
		}
		return null;
	}

	@Override
	public int size() {
		return size;
	}

	public String show() {
		Entry<K, V>[] tempTable = table;
		StringBuilder sb = new StringBuilder();
		for (int i = 0; i < tempTable.length; i++) {
			sb.append(i + " :      ");
			Entry<K, V> first = tempTable[i];
			while (tempTable[i] != null) {
				sb.append(tempTable[i].toString());
				sb.append("  ");
				tempTable[i] = tempTable[i].next;
			}
			tempTable[i] = first;
			sb.append("\n");
		}
		return sb.toString();
	}

