int ** Alocare(int linii,int coloane) {
	int **pointer;
	if ((pointer = (int **)malloc(sizeof(int *)*linii)) == NULL) {
		printf("Nu s-a gasit memorie.");
		_getch();
		exit(0);
	}
	for (int i = 0;i <linii;i++)
		if ((pointer[i] = (int *)malloc(sizeof(int)*coloane)) == NULL) {
			printf("nu s-a gasit memorie.");
			_getch();
			exit(0);
		}

	return pointer;
}

void CitireMatrice(int linii,int coloane,int **pointer){
	
	for (int i = 0;i < linii;i++) {
		for (int j = 0;j < coloane;j++) {
			printf("matrice[%d][%d]= ", i, j);
			scanf_s("%d", &pointer[i][j]); 
		}
	}
}

void InmultireMatrice(int linii,int coloane,int coloane2,int **a,int **b,int **c){
	for (int i = 0;i < linii; i++)
		for (int j = 0;j < coloane2;j++) {
			c[i][j] = 0;
			for (int k = 0;k < coloane; k++)
				c[i][j] = c[i][j] + a[i][k] * b[k][j];
		}


}

void AfisareMatrice(int linii, int coloane, int **pointer) {
	for (int i = 0;i < linii;i++) {
		for (int j = 0;j < coloane;j++) {
			printf("%d ", pointer[i][j]);
		}
		printf("\n");
	}

}

void main() {
	//citire afisare, inmultire matrici
	int **a, **b, **c, n, m, q, x;
	printf("Liniile primei matrici: ");
	scanf_s("%d", &n);
	printf("Coloanele primei matrici: ");
	scanf_s("%d", &m);
	printf("Liniile matricei a doua: ");
	scanf_s("%d", &q);
	while (m != q) {
		printf("Coloanele de la matricea unu trebuie sa fie egal cu nr de lini din matricea 2: ");
		scanf_s("%d ", &q);
	}
	printf("Coloanele matricei a doua: ");
	scanf_s("%d", &x);

	a = Alocare(n, m);
	CitireMatrice(n, m, a);
	printf("\nAfisare matrice:\n");
	AfisareMatrice(n, m, a);

	b = Alocare(q, x);
	CitireMatrice(q, x, b);
	printf("\nAfisare matrice:\n");
	AfisareMatrice(q, x, b);

	c = Alocare(n, x);
	InmultireMatrice(n, m, x, a, b, c);
	printf("\nAfisare matrice:\n");
	AfisareMatrice(n, x, c);

	
	_getch();
}
