#include <iostream>
using namespace std;
 
int main(){
	ios::sync_with_stdio(false);
	int t; 
	cin >> t;
	while (t--) {
		int n, m, cost1=0, cost2=0;
		cin >> n >> m;
		char niz[105][105], nizR[105][105], nizG[105][105]; // [n][m]
		for (int j = 0; j < n; j++) {
			for (int i = 0; i < m; i++)
				cin >> niz[j][i];	
		}
		for (int j = 0; j < n; j++) {
			for (int i = 0; i < m; i++)
				//PRVA KOLONA I PRVA VRSTA
				if (i == 0 && j == 0) {
					nizR[j][i] = 'R';
					nizG[j][i] = 'G';
				}
				else if (i == 0 && j > 0) {
					switch (nizR[j - 1][i]) {
						case 'R':
							nizR[j][i] = 'G';
							break;
 
						case 'G':
							nizR[j][i] = 'R';
							break;
					}
 
					switch (nizG[j - 1][i]) {
						case 'R':
							nizG[j][i] = 'G';
							break;
 
						case 'G':
							nizG[j][i] = 'R';
							break;
					}
				}

				else
				{
					switch (nizR[j][i-1]) {
						case 'R':
							nizR[j][i] = 'G';
							break;
 
						case 'G':
							nizR[j][i] = 'R';
							break;
					}
 
					switch (nizG[j][i-1]) {
						case 'R':
							nizG[j][i] = 'G';
							break;
 
						case 'G':
							nizG[j][i] = 'R';
							break;
					}
				}
		}

		for (int j = 0; j < n; j++) {
			for (int i = 0; i < m; i++){
				if (niz[j][i] != nizR[j][i]) {
					if (nizR[j][i] == 'G') cost1 += 5;
					else if (nizR[j][i] == 'R') cost1 += 3;
				}
 
				if (niz[j][i] != nizG[j][i]) {
					if (nizG[j][i] == 'G') cost2 += 5;
					else if (nizG[j][i] == 'R') cost2 += 3;
				}
			}
		}

		if (cost2 < cost1) cost1 = cost2;
		cout << cost1 << endl;
	}
	//system("pause");
	return 0;
}
