void hexa_4bytes (uint32_t index, uint32_t num)
{
    arena [index + 3] = (num >> 24) & 0xFF;
    arena [index + 2] = (num >> 16) & 0xFF;
    arena [index + 1] = (num >> 8) & 0XFF;
    arena [index] = num & 0xFF;
}

