#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <sys/ioctl.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <sys/select.h>
#include <sys/time.h>
#include <errno.h>
#include <string.h>

int initGpio(char[]);
int disableGpio(char[]);
int setGpioVal(char[], char[]);
int gpioDir(char[], char[]);
char status[1024], buff[6];
int fd, numread;

int main(void)
{
initGpio("33\n");
initGpio("34\n");
initGpio("35\n");
printf("Setting Pins\n");
gpioDir("out","33");
gpioDir("out","34");
gpioDir("out","35");
setGpioVal("1", "33"); //disabling motor at start

sleep(5);
printf("Sleeping for 5 sec");

fd = open("/tmp/motorstat", O_RDONLY);
printf("Fd Status is : %d\n", fd);
while(1)
{

printf("Fd IN WHILE Status is : %d\n", fd);

numread = read(fd, status, 6);
status[numread] = '\0';
printf("Motorstat is : %s \n", status);
sprintf(buff,"%s", status);



if ((strcmp(status, "START")) == 0){
	printf("Started Motor!! \n");
setGpioVal("0", "33");//enable pin
setGpioVal("1", "35");//dir pin
}

if ((strcmp(status, "STOPP")) == 0){
	printf("Stopping Motor!!\n");
setGpioVal("1", "33");
setGpioVal("1", "35");
}

else {
	printf("In Else Now!!\n");
	setGpioVal("1", "33");
	setGpioVal("1", "35");
}

setGpioVal("1", "34");
printf("Sleeping!! \t");
usleep(500*1000);
setGpioVal("0", "34");
printf("Sleeping Twice!!\n");
usleep(500*1000);

}
close(fd);
return 0;
}

/////////////////////////////////////////////////////////////////// initilizing gpio pin
int initGpio(char ch[])
{
int fd;
fd=open("/sys/class/gpio/export", O_WRONLY|O_APPEND);
if (fd < 0) {
	printf("didn't get the file!!");
        return -1;
    }
write(fd, ch, sizeof(ch));
close(fd);
return 0;
}

/////////////////////////////////////////////////////////////// disabling gpio pin
int disableGpio(char ch[])
{
int fd;
fd=open("/sys/class/gpio/unexport", O_WRONLY|O_APPEND);
if (fd < 0) {
	printf("didn't get the file!!");
        return -1;
    }
write(fd, ch, sizeof(ch));
close(fd);
return 0;
}

///////////////////////////////////////////////////////////////////////	setting gpio pin value to 0 or 1
int setGpioVal(char pin_state[1], char ch[])
{
int fd, ret;
char buff[1024];
ret = sprintf(buff,"/sys/class/gpio/gpio%s/value", ch);
//ret = sprintf(buff,"/sys/class/gpio/gpio%s/value",ch);
//printf("Buffer length is %d\n", ret);
//printf("Buffer is %s", buff);
fd=open(buff, O_WRONLY);
if (fd < 0) {
//	printf("In setGpio \nDidn't get the file!!\n");
        return -1;
    }
//write(fd, set, 1);
write(fd, pin_state, 1);
close(fd);
return 0;
}
//////////////////////////////////////////////////////////////////// setting gpio pin direction to in or out

int gpioDir(char pin_state[3], char ch[])
{
int fd, ret;
char buff[1024];
ret = sprintf(buff,"/sys/class/gpio/gpio%s/direction", ch);
//ret = sprintf(buff,"/sys/class/gpio/gpio%s/value",ch);
//printf("Buffer length is %d\n", ret);
//printf("Buffer is %s", buff);
fd=open(buff, O_WRONLY);
if (fd < 0) {
	printf("In setGpio \nDidn't get the file!!\n");
        return -1;
    }
//write(fd, set, 1);
write(fd, pin_state, 3);
close(fd);
return 0;
}
