static void __near r_tau0_channel0_interrupt(void)
{
    /* Start user code. Do not edit comment generated here */
	// DAC出力
	P1_bit.no1 = 0;
	DACD = g_dac_data[g_dac_set_count];
	check = g_dac_set_count%C_PGA_SAMPLING;
	if(g_dac_set_count/C_PGA_SAMPLING == 0)
	{
		g_pga_get_time = 1;
		R_PGA_DSAD_Start(); 	//starts the PGA and DS A/D converter.	
	}
	if (g_dac_set_count >= C_DAC_SET_NUM)
	{
		g_dac_set_count = 0;
	}
	
	g_dac_set_count++;

	P1_bit.no1 = 1;
