PROGRAM _CYCLIC
	
	CASE JAVA_CONNECT OF
	
	0:	
	TCPOpen.enable := TRUE;
	TCPOpen.port := 12345;
	TCPOpen();
	IF TCPOpen.status = ERR_OK THEN
		JAVA_CONNECT := 10;
	END_IF
	
	10:
	TCPServer.enable := TRUE;
	TCPServer.ident := TCPOpen.ident;
	TCPServer();
	IF TCPServer.status = ERR_OK THEN
		JAVA_CONNECT := 20;
	END_IF
			
	20:
	TCPRecv.enable := TRUE;
	TCPRecv.ident := TCPServer.identclnt;
	TCPRecv.pData := ADR(receiveData);
			TCPRecv.datamax := SIZEOF(receiveData);
			TCPRecv();
			IF TCPRecv.status = ERR_OK THEN	
				FOR i := 0 TO 9 BY 1 DO
					input[i] := receiveData[i];
					receiveData[i] := '0';
				END_FOR
				
			
			//	JAVA_CONNECT := 30;
			END_IF	
		
			



			
	30:		
		TCPClose.enable := TRUE;
		TCPClose.ident := TCPServer.identclnt;
		TCPClose();
		IF TCPClose.status = ERR_OK THEN
			TCPServer(enable := FALSE);
			JAVA_CONNECT := 10;
			END_IF
		END_CASE
			
	
	 
END_PROGRAM

PROGRAM _EXIT
			TCPClose.enable := TRUE;
			TCPClose.ident := TCPServer.identclnt;
			TCPClose();
			TCPClose.enable := TRUE;
			TCPClose.ident := TCPClose.ident;
	 
END_PROGRAM

void boyer(string k, string t)
{
	int x = t.length();
	int d = 0;
	for(int i = 0; i < x; i++)
	{
		string z = "";
		for(int j = d; j < x; j++)
		{
			z+= t[j];
		}
		
		d+= _boyer(k, z) + k.length();
		cout << z << endl;	
		if(z.length() > 0)
			cout << d - k.length() << endl;
	}
}
