const INP='SIMPLE.INP';
OUTP='SIMPLE.OUT';
MAX=20;
type
node = ^clm;
clm = record
cell:array[1..MAX] of node;
range:array[1..MAX] of longint;
value:longint;
height:byte;
end;
skplst = record
head,tail:node;
end;
var f:text;
sk:skplst;
z:array[1..MAX] of node;
a:array[1..100000] of longint;
procedure rand(var k:byte);
begin
k:=1;
while (k<MAX)and (random(2)=1) do INC(k);
end;
procedure push(k:longint);
var y:node;
i:byte;
begin
new(y);
y^.value:=k;
rand(y^.height);
for i:=1 to y^.height do
begin
z[i]^.cell[i]:=y;
y^.cell[i]:=sk.tail;
y^.range[i]:=1;
z[i]:=y;
end;
for i:=y^.height+1 to MAX do
INC(z[i]^.range[i]);
end;
procedure move1(u,v:longint);
var x:node;
t:longint;
i:byte;
y:array[1..MAX] of node;
begin
x:=sk.head;
t:=0;
for i:=MAX downto 1 do
begin
while (x^.cell[i]<>sk.tail) and (t+x^.range[i]<u) do
begin
INC(t,x^.range[i]);
x:=x^.cell[i];
end;
y[i]:=x;
end;
for i:=1 to MAX do z[i]:=sk.tail;
INC(t);
x:=x^.cell[1];
while t<=v do
begin
i:=x^.height;
while (i>1) and (t+x^.range[i]>v) do
begin
z[i]:=x;
DEC(i);
end;
z[i]:=x;
if t+x^.range[i]>v then break;
INC(t,x^.range[i]);
x:=x^.cell[i];
end;
//y: previous nodes, z: last nodes
for i:=MAX downto 1 do
begin
x:=sk.head^.cell[i];
if x=sk.tail then continue;
sk.head^.cell[i]:=y[i]^.cell[i];
if z[i]=sk.tail then y[i]^.cell[i]:=z[i] else
y[i]^.cell[i]:=z[i]^.cell[i];
z[i]^.cell[i]:=x;
end;
y[1]^.range[1]:=1;
for i:=2 to MAX do
begin
y[i]^.range[i]:=0;
x:=y[i];
while x<>y[i]^.cell[i] do
begin
INC(y[i]^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
z[1]^.range[1]:=1;
for i:=2 to MAX do
if z[i]=sk.tail then break
else begin
z[i]^.range[i]:=0;
x:=z[i];
while x<>z[i]^.cell[i] do
begin
INC(z[i]^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
sk.head^.range[1]:=1;
for i:=2 to MAX do
begin
sk.head^.range[i]:=0;
x:=sk.head;
while x<>sk.head^.cell[i] do
begin
INC(sk.head^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
end;
procedure move2(u,v:longint);
var x:node;
t:longint;
i,maxw:byte;
y,w:array[1..MAX] of node;
begin
x:=sk.head;
t:=0;
for i:=MAX downto 1 do
begin
while (x^.cell[i]<>sk.tail) and (t+x^.range[i]<u) do
begin
INC(t,x^.range[i]);
x:=x^.cell[i];
end;
y[i]:=x;
end;
for i:=1 to MAX do z[i]:=sk.tail;
INC(t);
x:=x^.cell[1];
while t<=v do
begin
i:=x^.height;
while (i>1) and (t+x^.range[i]>v) do
begin
z[i]:=x;
DEC(i);
end;
z[i]:=x;
if t+x^.range[i]>v then break;
INC(t,x^.range[i]);
x:=x^.cell[i];
end;
x:=x^.cell[1];
maxw:=0;
while x<>sk.tail do
begin
i:=x^.height;
if maxw<i then maxw:=i;
if (i>1) and (x^.cell[i]=sk.tail) then
begin
w[i]:=x;
DEC(i);
end;
w[i]:=x;
if x^.cell[i]=sk.tail then break;
x:=x^.cell[i];
end;
//y: previous nodes, z: last nodes
for i:=MAX downto 1 do
begin
x:=z[i]^.cell[i];
if i<=maxw then
w[i]^.cell[i]:=y[i]^.cell[i];
if z[i]^.cell[i]<>sk.tail then
y[i]^.cell[i]:=z[i]^.cell[i];
z[i]^.cell[i]:=sk.tail;
end;
y[1]^.range[1]:=1;
for i:=2 to MAX do
begin
y[i]^.range[i]:=0;
x:=y[i];
while x<>y[i]^.cell[i] do
begin
INC(y[i]^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
z[1]^.range[1]:=1;
for i:=2 to MAX do
if z[i]=sk.tail then break
else begin
z[i]^.range[i]:=0;
x:=z[i];
while x<>z[i]^.cell[i] do
begin
INC(z[i]^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
sk.head^.range[1]:=1;
for i:=2 to MAX do
begin
sk.head^.range[i]:=0;
x:=sk.head;
while x<>sk.head^.cell[i] do
begin
INC(sk.head^.range[i],x^.range[i-1]);
x:=x^.cell[i-1];
end;
end;
end;
procedure init;
var i:byte;
begin
new(sk.head);
new(sk.tail);
sk.head^.height:=MAX;
sk.tail^.height:=MAX;
for i:=1 to MAX do
begin
z[i]:=sk.head;
sk.head^.cell[i]:=sk.tail;
sk.head^.range[i]:=1;
end;
end;
procedure nhap;
var n,m,i,t,u,v:longint;
begin
init;
assign(f,INP);
reset(f);
readln(f,n,m);
for i:=1 to n do
begin
read(f,t);
push(t);
end;
readln(f);
for i:=1 to m do
begin
readln(f,t,u,v);
if t=1 then move1(u,v);
//else move2(u,v);
end;
close(f);
end;
procedure xuat;
var i,n:longint;
x:node;
begin
assign(f,OUTP);
rewrite(f);
x:=sk.head;
n:=0;
while x^.cell[1]<>sk.tail do
begin
INC(n);
x:=x^.cell[1];
a[n]:=x^.value;
end;
writeln(f,abs(a[n]-a[1]));
for i:=1 to n do write(f,a[i],#32);
close(f);
end;
begin
nhap;
xuat;
end.

@Override
    public void onBlockRedstoneChange( BlockRedstoneEvent event ) {
        Block block = event.getBlock();
        // TypeId 74 == GLOWING_REDSTONE_ORE
        // TpyeId 73 == REDSTONE_ORE
        if ( event.getNewCurrent() == 0 && block.getTypeId() == 74 ) {
            block.setTypeId(Material.REDSTONE_ORE.getId());
        }
        int newPower = event.getNewCurrent() - (event.getNewCurrent() == 0 ? 0 : 1);
        if ( block.getState() instanceof RedstoneWire || block.getTypeId() == 73 || block.getTypeId() == 74 || block.getData() > 0 ) {
            for (BlockFace face : EnumSet.of(BlockFace.DOWN, BlockFace.UP, BlockFace.WEST, BlockFace.NORTH, BlockFace.EAST, BlockFace.SOUTH)) {
                Block nextBlock = block.getFace(face);
                if ( nextBlock.getTypeId() == 73 || nextBlock.getTypeId() == 74 || nextBlock.getState() instanceof RedstoneWire ) {
                    int oldPower = 0;
                    if ( nextBlock.getTypeId() == Material.REDSTONE_ORE.getId() ) {
                        nextBlock.setTypeId(Material.GLOWING_REDSTONE_ORE.getId());
                        oldPower = 0;
                        getRedstoneOre.put(nextBlock, newPower);
                        plugin.getServer().getPluginManager().callEvent(new BlockRedstoneEvent(nextBlock, oldPower, newPower));
                    } else if ( nextBlock.getTypeId() == Material.GLOWING_REDSTONE_ORE.getId() ) {
                        oldPower = getRedstoneOre.get(nextBlock);
                        getRedstoneOre.put(nextBlock, newPower);
                        plugin.getServer().getPluginManager().callEvent(new BlockRedstoneEvent(nextBlock, oldPower, newPower));
                    } else {
                        oldPower = nextBlock.getData();
                        plugin.getServer().getPluginManager().callEvent(new BlockRedstoneEvent(nextBlock, oldPower, newPower));
                    }
                }
            }
        }
    }

