PROCEDURE `zmien`(IN id INT, IN nick varchar(30), IN steamid varchar(40), IN nazwa varchar(30))
BEGIN
if (nick='admin' or nick = 'root') then
 SIGNAL SQLSTATE '68712'
 SET MESSAGE_TEXT = 'zakazany nick gracza';
 end if;
Update gracz set Nick=nick, SteamID=steamid, NazwaKontaWGrze=nazwa
Where IDGracza = id;
END
