// Client Conole Application
// Note the methods to call the server have been generated as part of the REST API client and have 
//   application specific names e.g. client.Products.PostProductWithHttpMessagesAsync()

using Microsoft.Rest;
using ProductStoreClient.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace ProductStoreClient
{
    class Program
    {
        static void Main(string[] args)
        {
            RunAsync().Wait();
        }

        static async Task RunAsync()
        {



            int menuchoice = 0;
            while (menuchoice != 7)
            {
                #region MENU_OPTION
                Console.WriteLine("Press <ENTER> after each HTTP request to step through.");
                Console.WriteLine("1. List all sample entities");
                Console.WriteLine("2. List a specific entry");
                Console.WriteLine("3. Create a new sample entity");
                Console.WriteLine("4. Delete a new sample entity");
                Console.WriteLine("5. Update a sample entity");
                Console.WriteLine("6. Upload an mp3 from local file system");
                Console.WriteLine("7. Download an mp3 sample file");
                Console.WriteLine();
                #endregion
                // Pick URL location of service up from metadata
                ProductStore client = new ProductStore(new AnonymousCredential());

                // Alternatively set URL location of service explicitly
                //ProductStore client = new ProductStore(new Uri("http://localhost:50001/"), new AnonymousCredential());

                menuchoice = int.Parse(Console.ReadLine());

                switch (menuchoice)
                {
                    case 1:

                        //retrieve all table data
                        #region GET_all
                        // HTTP GET - get all products
                        Console.WriteLine("Display table contents");
                        // GetWithHttpMessagesAsync() generated as part of REST API client.
                        HttpOperationResponse<IList<Product>> getListResponse = await client.Products.GetWithHttpMessagesAsync();
                        Console.WriteLine("GET complete; status code: " + getListResponse.Response.StatusCode);
                        Console.WriteLine("Products:");
                        foreach (Product product in getListResponse.Body)
                        {
                            Console.WriteLine("{0}\t\t{1}\t\t${2}\t\t{3}", product.ProductID, product.Title, product.Artist, product.SampleBlobURL);
                        }
                        Console.WriteLine();
                        Console.WriteLine();
                        #endregion
                        break;

                    case 2:

                        #region GET_specific
                        // HTTP GET - get all products
                        Console.WriteLine("Enter a row id");
                        int key = int.Parse(Console.ReadLine());
                        // GetWithHttpMessagesAsync() generated as part of REST API client.
                        HttpOperationResponse<IList<Product>> getListResponse1 = await client.Products.GetWithHttpMessagesAsync();
                        Console.WriteLine("GET complete; status code: " + getListResponse1.Response.StatusCode);
                        Console.WriteLine("Samples:");
                        foreach (Product product in getListResponse1.Body)
                        {
                            if (key == int.Parse(product.ProductID))
                            {
                                Console.WriteLine("{0}\t\t{1}\t\t${2}\t\t{3}", product.ProductID, product.Title, product.Artist, product.SampleBlobURL);
                            }

                        }
                        Console.WriteLine();
                        Console.WriteLine();
                        #endregion
                        break;

                    case 3:

                        //post
                        #region POST 
                        // HTTP POST - create a new product and obtain the new resource URL (location)
                        Console.WriteLine("Title, Artist then Samplebloburl");
                        Product UserEntity = new Product() { Title = Console.ReadLine(), Artist = Console.ReadLine(), SampleBlobURL = Console.ReadLine() };
                        // PostProductWithHttpMessagesAsync() generated as part of REST API client.
                        HttpOperationResponse<Product> postResponse = await client.Products.PostProductWithHttpMessagesAsync(UserEntity);
                       // Console.WriteLine("POST complete; status code: {0}; location: {1}", postResponse.Response.StatusCode, postResponse.Response.Headers.Location);
                        Console.WriteLine();
                        Console.WriteLine();
                        #endregion
                        break;

                    case 4:

                        #region DELETE
                        // HTTP DELETE - delete a product
                        //String id = postResponse.Response.Headers.Location.LocalPath.Split('/').Last();

                        Console.WriteLine("Test DELETE");
                        try
                        {
                            // DeleteProductWithHttpMessagesAsync() generated as part of REST API client.
                            HttpOperationResponse deleteResponse1 = await client.Products.DeleteProductWithHttpMessagesAsync((Console.ReadLine()));
                            Console.WriteLine("DELETE complete; status code: " + deleteResponse1.Response.StatusCode);
                        }
                        catch (Exception e)
                        {
                            Console.WriteLine("HTTP error: " + e.Message); // NotFound will throw this...
                        }
                        #endregion
                        break;
                    case 5:

                        //update specific
                        #region Update 
                        // HTTP POST - create a new product and obtain the new resource URL (location)
                        Console.WriteLine("Enter the following on seperate lines: Existing ID, Title, Artist then Samplebloburl");

                        String id = Console.ReadLine();

                        Product UpdateExisting = new Product() { ProductID = id, Title = Console.ReadLine(), Artist = Console.ReadLine(), SampleBlobURL = Console.ReadLine() };
                        // PostProductWithHttpMessagesAsync() generated as part of REST API client.
                        // HttpOperationResponse deleteResponse = await client.Products.DeleteProductWithHttpMessagesAsync(id);
                        var postResponse1 = await client.Products.PutProductWithHttpMessagesAsync(id, UpdateExisting);
                        Console.WriteLine("POST complete; status code: {0}; location: {1}", postResponse1.Response.StatusCode, postResponse1.Response.Headers.Location);
                        Console.WriteLine();
                        Console.WriteLine();
                        #endregion
                        break;

                    case 6:
                        #region Upload
                        //update
                        Console.WriteLine("Thing F has been done");
                        break;
                    #endregion
                    case 7:

                        #region download
                        //download
                        return;
                    #endregion

                    //edit
                    default:
                        Console.WriteLine("Invalid selection, enter 1-7");
                        break;
                }
            }


        }
    }
}
