.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    font-size: 13px;
    color: rgb(83, 83, 83);
    background: #ff8900;
    background: -webkit-linear-gradient(to right, #ff8900, #ffe600);
    background: linear-gradient(to right, #ff8900, #ffe600);
    box-shadow: 0 19px 38px rgba(0,0,0,0.30), 0 15px 12px rgba(0,0,0,0.22);
    padding: 8px;
    margin-bottom: 8px;
    border: 0px solid rgb(226, 226, 226);
    font-weight: 600;
    margin-right: -9px;
    margin-top: -9px;
    margin-left: -9px;
}
